import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const menuButton = {
  backgroundColor: '#2c3e50',
  color: '#ecf0f1',
  border: 'none',
  padding: '10px 15px',
  fontSize: '16px',
  borderRadius: '6px',
  cursor: 'pointer',
  width: '100%',
  textAlign: 'left',
  marginBottom: '10px',
  transition: 'background-color 0.3s',
};

export default function Main() {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // --- États pour la recherche ---
  const [destination, setDestination] = useState('Tunisie');
  const [arrival, setArrival] = useState('');
  const [departure, setDeparture] = useState('');
  const [adults, setAdults] = useState(2);
  const [children, setChildren] = useState(0);
  const [rooms, setRooms] = useState(1);

  const handleSearch = () => {
    console.log({
      destination,
      arrival,
      departure,
      adults,
      children,
      rooms,
    });
    // Tu peux ajouter ici navigate('/resultats') ou envoyer la recherche au backend
  };

  return (
    <div
      style={{
        display: 'flex',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        height: '100vh',
      }}
    >
      {/* -------- MENU -------- */}
      <nav
        style={{
          width: '220px',
          backgroundColor: '#2c3e50',
          padding: '30px 20px',
          boxSizing: 'border-box',
          color: '#ecf0f1',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between',
        }}
      >
        <div>
          <h2
            style={{
              marginBottom: '30px',
              borderBottom: '2px solid #2980b9',
              paddingBottom: '10px',
            }}
          >
            Menu Utilisateur
          </h2>

          <button
            style={menuButton}
            onClick={() => navigate('/user')}
            onMouseEnter={(e) =>
              (e.target.style.backgroundColor = '#2980b9')
            }
            onMouseLeave={(e) =>
              (e.target.style.backgroundColor = '#2c3e50')
            }
          >
            🏠 Accueil
          </button>

          <button
            style={menuButton}
            onClick={() => navigate('/user/search')}
            onMouseEnter={(e) =>
              (e.target.style.backgroundColor = '#2980b9')
            }
            onMouseLeave={(e) =>
              (e.target.style.backgroundColor = '#2c3e50')
            }
          >
            🔍 Recherche Hôtels
          </button>

          <button
            style={menuButton}
            onClick={() => navigate('/reports/Reports')}
            onMouseEnter={(e) =>
              (e.target.style.backgroundColor = '#2980b9')
            }
            onMouseLeave={(e) =>
              (e.target.style.backgroundColor = '#2c3e50')
            }
          >
            📊 Rapport
          </button>

          <button
            style={menuButton}
            onClick={() => navigate('/user/profile')}
            onMouseEnter={(e) =>
              (e.target.style.backgroundColor = '#2980b9')
            }
            onMouseLeave={(e) =>
              (e.target.style.backgroundColor = '#2c3e50')
            }
          >
            👤 Profil
          </button>

          <button
            style={menuButton}
            onClick={() => navigate('/user/bookings')}
            onMouseEnter={(e) =>
              (e.target.style.backgroundColor = '#2980b9')
            }
            onMouseLeave={(e) =>
              (e.target.style.backgroundColor = '#2c3e50')
            }
          >
            📅 Réservations
          </button>

          <button
            style={menuButton}
            onClick={() => navigate('/user/favorites')}
            onMouseEnter={(e) =>
              (e.target.style.backgroundColor = '#2980b9')
            }
            onMouseLeave={(e) =>
              (e.target.style.backgroundColor = '#2c3e50')
            }
          >
            ⭐ Favoris
          </button>

          <button
            style={menuButton}
            onClick={() => navigate('/user/contact')}
            onMouseEnter={(e) =>
              (e.target.style.backgroundColor = '#2980b9')
            }
            onMouseLeave={(e) =>
              (e.target.style.backgroundColor = '#2c3e50')
            }
          >
            📞 Contact
          </button>

          <button
            style={menuButton}
            onClick={() => navigate('/user/help')}
            onMouseEnter={(e) =>
              (e.target.style.backgroundColor = '#2980b9')
            }
            onMouseLeave={(e) =>
              (e.target.style.backgroundColor = '#2c3e50')
            }
          >
            ❓ Aide / FAQ
          </button>

          <button
            style={menuButton}
            onClick={() => navigate('/user/settings')}
            onMouseEnter={(e) =>
              (e.target.style.backgroundColor = '#2980b9')
            }
            onMouseLeave={(e) =>
              (e.target.style.backgroundColor = '#2c3e50')
            }
          >
            ⚙️ Paramètres
          </button>
        </div>

        <button
          onClick={handleLogout}
          style={{
            backgroundColor: '#c0392b',
            color: '#fff',
            border: 'none',
            padding: '10px 15px',
            fontSize: '16px',
            borderRadius: '6px',
            cursor: 'pointer',
            transition: 'background-color 0.3s',
          }}
          onMouseEnter={(e) =>
            (e.target.style.backgroundColor = '#e74c3c')
          }
          onMouseLeave={(e) =>
            (e.target.style.backgroundColor = '#c0392b')
          }
        >
          🔓 Déconnexion
        </button>
      </nav>

      {/* -------- CONTENU PRINCIPAL -------- */}
      <main
        style={{
          padding: '30px',
          flexGrow: 1,
          backgroundColor: '#f5f7fa',
        }}
      >
        <h1>Hôtels en Tunisie</h1>
        <p>
          Indiquez vos dates et faites votre choix parmi des milliers
          d'hôtels !
        </p>

        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            gap: '10px',
            marginTop: '20px',
          }}
        >
          <input
            type="text"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            placeholder="Destination"
            style={{ padding: '10px', minWidth: '180px' }}
          />

          <input
            type="date"
            value={arrival}
            onChange={(e) => setArrival(e.target.value)}
            style={{ padding: '10px' }}
          />

          <input
            type="date"
            value={departure}
            onChange={(e) => setDeparture(e.target.value)}
            style={{ padding: '10px' }}
          />

          <input
            type="number"
            value={adults}
            onChange={(e) => setAdults(e.target.value)}
            min="1"
            placeholder="Adultes"
            style={{ padding: '10px', width: '80px' }}
          />

          <input
            type="number"
            value={children}
            onChange={(e) => setChildren(e.target.value)}
            min="0"
            placeholder="Enfants"
            style={{ padding: '10px', width: '80px' }}
          />

          <input
            type="number"
            value={rooms}
            onChange={(e) => setRooms(e.target.value)}
            min="1"
            placeholder="Chambres"
            style={{ padding: '10px', width: '80px' }}
          />

          <button
            onClick={handleSearch}
            style={{
              padding: '10px 20px',
              backgroundColor: '#2980b9',
              color: '#fff',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
            }}
          >
            🔍 Rechercher
          </button>
        </div>
      </main>
    </div>
  );
}
