import React from 'react';

export default function Dashboard() {
  const stats = [
    { title: 'Utilisateurs inscrits', value: 150, icon: 'üë•', color: '#4ade80' }, // vert clair
    { title: 'R√©servations actives', value: 45, icon: 'üìÖ', color: '#60a5fa' },    // bleu
    { title: 'Commandes en cours', value: 12, icon: 'üöö', color: '#facc15' },      // jaune
    { title: 'Messages non lus', value: 3, icon: '‚úâÔ∏è', color: '#f87171' },        // rouge clair
  ];

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={{ margin: 0 }}>Tableau de bord Admin</h1>
        <p>G√®re ton application facilement depuis ici.</p>
      </header>

      <section style={styles.statsGrid}>
        {stats.map(({ title, value, icon, color }) => (
          <div key={title} style={{ ...styles.card, borderTop: `4px solid ${color}` }}>
            <div style={styles.cardIcon}>{icon}</div>
            <h3>{title}</h3>
            <p style={styles.statValue}>{value}</p>
          </div>
        ))}
      </section>

      <section style={styles.actions}>
        <h2>Actions rapides</h2>
        <div>
          <button style={{ ...styles.button, backgroundColor: '#2563eb' }} onClick={() => alert('G√©rer les utilisateurs')}>
            G√©rer les utilisateurs
          </button>
          <button style={{ ...styles.button, backgroundColor: '#16a34a' }} onClick={() => alert('Voir les r√©servations')}>
            Voir les r√©servations
          </button>
        </div>
      </section>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: 960,
    margin: 'auto',
    padding: 20,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    color: '#333',
  },
  header: {
    marginBottom: 30,
  },
  statsGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit,minmax(220px,1fr))',
    gap: 20,
  },
  card: {
    backgroundColor: '#fff',
    padding: 20,
    borderRadius: 10,
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    textAlign: 'center',
  },
  cardIcon: {
    fontSize: 36,
    marginBottom: 10,
  },
  statValue: {
    fontSize: 32,
    fontWeight: 'bold',
    marginTop: 8,
  },
  actions: {
    marginTop: 40,
  },
  button: {
    color: 'white',
    border: 'none',
    padding: '12px 24px',
    marginRight: 15,
    fontSize: 16,
    borderRadius: 6,
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
};
