import React, { useState } from 'react';
import LogoutButton from './components/LogoutButton.js';

export default function AdminDashboard() {
  const [activeMenu, setActiveMenu] = useState('users'); // menu actif

  const renderContent = () => {
    switch (activeMenu) {
      case 'users':
        return <div>
          <h2>Gestion des utilisateurs</h2>
          {/* Ici tu mettras ta table utilisateurs, ajout, modif, suppr */}
          <p>Ici tu pourras gérer les utilisateurs (ajouter, modifier, supprimer)</p>
        </div>;

      case 'settings':
        return <div>
          <h2>Paramètres</h2>
          <p>Page des paramètres à développer</p>
        </div>;

      case 'reports':
        return <div>
          <h2>Rapports</h2>
          <p>Page des rapports à développer</p>
        </div>;

      default:
        return <p>Sélectionne un menu.</p>;
    }
  };

  return (
    <div style={{ display: 'flex', height: '100vh', fontFamily: 'Arial, sans-serif' }}>
      {/* Menu latéral */}
      <nav style={{
        width: 200,
        backgroundColor: '#6a4c93',
        color: 'white',
        padding: 20,
        display: 'flex',
        flexDirection: 'column',
        gap: 15,
      }}>
        <h1 style={{ fontSize: '1.5rem', marginBottom: 20 }}>Admin</h1>
        <button
          onClick={() => setActiveMenu('users')}
          style={{
            padding: 10,
            backgroundColor: activeMenu === 'users' ? '#9c89b8' : 'transparent',
            border: 'none',
            color: 'white',
            cursor: 'pointer',
            textAlign: 'left',
            borderRadius: 5,
          }}
        >
          Utilisateurs
        </button>
        <button
          onClick={() => setActiveMenu('settings')}
          style={{
            padding: 10,
            backgroundColor: activeMenu === 'settings' ? '#9c89b8' : 'transparent',
            border: 'none',
            color: 'white',
            cursor: 'pointer',
            textAlign: 'left',
            borderRadius: 5,
          }}
        >
          Paramètres
        </button>
        <button
          onClick={() => setActiveMenu('reports')}
          style={{
            padding: 10,
            backgroundColor: activeMenu === 'reports' ? '#9c89b8' : 'transparent',
            border: 'none',
            color: 'white',
            cursor: 'pointer',
            textAlign: 'left',
            borderRadius: 5,
          }}
        >
          Rapports
        </button>
        
        <div style={{ marginTop: 'auto' }}>
          <LogoutButton />
        </div>
      </nav>

      {/* Contenu principal */}
      <main style={{ flex: 1, padding: 30, backgroundColor: '#f0f0f0' }}>
        {renderContent()}
      </main>
    </div>
  );
}
