import React from 'react';
import { Outlet, useNavigate, useLocation } from 'react-router-dom';

export default function AdminDashboard() {
  const navigate = useNavigate();
  const location = useLocation();

  const menuButton = {
    backgroundColor: 'transparent',
    border: 'none',
    color: 'white',
    textAlign: 'left',
    padding: '10px 15px',
    fontSize: 18,
    cursor: 'pointer',
    borderRadius: 6,
    fontWeight: 'bold',
    transition: 'background-color 0.3s',
    width: '100%',
  };

  const getButtonStyle = (path) => ({
    ...menuButton,
    backgroundColor: location.pathname === path ? '#555' : 'transparent',
  });

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      {/* Sidebar */}
      <nav
        style={{
          width: 220,
          backgroundColor: '#3b3f6b',
          color: 'white',
          padding: 20,
          display: 'flex',
          flexDirection: 'column',
          gap: 15,
        }}
      >
        <h2 style={{ marginBottom: 30 }}>Admin Panel</h2>

        <button style={getButtonStyle('/admin')} onClick={() => navigate('/admin')}>
          üìä Dashboard
        </button>
        <button style={getButtonStyle('/admin/users')} onClick={() => navigate('/admin/users')}>
          üë• Utilisateurs
        </button>
        <button style={menuButton} onClick={() => alert('Notifications')}>
          üîî Notifications
        </button>
        <button style={menuButton} onClick={() => alert('Messages')}>
          ‚úâÔ∏è Messages
        </button>
        <button style={menuButton} onClick={() => alert('Reports')}>
          üìÑ Reports
        </button>

        <div style={{ marginTop: 'auto' }}>
          <button
            style={{ ...menuButton, backgroundColor: '#d9534f', width: '100%' }}
            onClick={handleLogout}
            onMouseOver={e => (e.currentTarget.style.backgroundColor = '#c9302c')}
            onMouseOut={e => (e.currentTarget.style.backgroundColor = '#d9534f')}
          >
            üö™ D√©connexion
          </button>
        </div>
      </nav>

      {/* Main content (outlet for nested routes) */}
      <main style={{ flex: 1, padding: 30, backgroundColor: '#f0f2f5' }}>
        <Outlet />
      </main>
    </div>
  );
}
