// OUI, ce composant frontend EST reli√© avec le backend !
// Explication :
// - Le formulaire appelle handleSubmit lors de la soumission.
// - handleSubmit envoie une requ√™te POST √† 'http://localhost:4000/api/users/login' (c'est l'URL du backend Express).
// - Le backend (voir Backend/Route/Users.js) re√ßoit cette requ√™te, v√©rifie l'utilisateur dans la base MySQL, et r√©pond avec le r√¥le.
// - Selon la r√©ponse du backend, le frontend redirige l'utilisateur ou affiche une erreur.
// Donc, le frontend et le backend sont bien reli√©s via cette API.

import React, { useState } from 'react';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Cette fonction envoie les infos au backend Express
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Appel du backend (Express) sur /api/users/login
      const response = await fetch('http://localhost:4000/api/users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        // Le backend r√©pond avec le r√¥le de l'utilisateur
        if (data.role === 'admin') {
          window.location.href = '/admin';
        } else if (data.role === 'user') {
          window.location.href = '/user';
        } else {
          setError('R√¥le utilisateur inconnu.');
        }
      } else {
        setError(data.message || '√âchec de la connexion.');
      }
    } catch (err) {
      console.error(err);
      // Am√©lioration du message d'erreur pour l'erreur de connexion refus√©e
      if (err.message.includes('ERR_CONNECTION_REFUSED')) {
        setError('Le serveur backend n\'est pas d√©marr√©. Veuillez d√©marrer le serveur sur le port 4000.');
      } else {
        setError('Erreur r√©seau. Veuillez r√©essayer.');
      }
    }
    setLoading(false);
  };

  // --- EXPLICATION DE L'ERREUR "ERR_CONNECTION_REFUSED" ---
  //
  // Quand tu vois cette erreur dans la console, cela signifie que :
  // - Le frontend essaie de se connecter √† http://localhost:4000/api/users/login
  // - MAIS aucun serveur n'√©coute sur le port 4000.
  //
  // üîß Solutions possibles :
  // 1. D√©marrer le serveur backend Express :
  //    - Ouvrir un terminal dans le dossier Backend
  //    - Ex√©cuter : npm start ou node server.js
  //    - V√©rifier que le serveur d√©marre sur le port 4000
  //
  // 2. V√©rifier que le serveur backend est bien configur√© :
  //    - Le fichier server.js doit contenir : app.listen(4000, ...)
  //    - V√©rifier qu'il n'y a pas d'erreur au d√©marrage
  //
  // 3. V√©rifier que le port 4000 n'est pas utilis√© par un autre processus :
  //    - Sur Windows : netstat -ano | findstr :4000
  //    - Sur Mac/Linux : lsof -i :4000
  //
  // 4. Si le serveur backend utilise un port diff√©rent :
  //    - Modifier l'URL dans le frontend pour correspondre au bon port
  //    - Ou modifier le port du backend pour utiliser 4000
  //
  // üí° Ordre de d√©marrage recommand√© :
  // 1. D√©marrer MySQL
  // 2. D√©marrer le serveur backend (npm start dans le dossier Backend)
  // 3. D√©marrer le frontend (npm start dans le dossier frontend)
  //
  // Pour plus d'aide, v√©rifie les logs du serveur backend dans le terminal.

  
  const bgStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #7f53ac 0%, #657ced 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: "'Poppins', sans-serif",
  };

  const cardStyle = {
    background: 'rgba(255,255,255,0.95)',
    boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.37)',
    borderRadius: 24,
    padding: '40px 32px',
    maxWidth: 380,
    width: '100%',
    textAlign: 'center',
    position: 'relative',
    overflow: 'hidden',
  };

  const inputStyle = {
    width: '100%',
    padding: '12px 16px',
    margin: '10px 0 20px 0',
    border: 'none',
    borderRadius: 8,
    background: '#f3f6fd',
    fontSize: 16,
    outline: 'none',
    boxShadow: '0 2px 8px rgba(127,83,172,0.07)',
    transition: 'box-shadow 0.2s',
  };

  const buttonStyle = {
    width: '100%',
    padding: '14px',
    background: 'linear-gradient(90deg, #7f53ac 0%, #657ced 100%)',
    color: '#fff',
    border: 'none',
    borderRadius: 8,
    fontWeight: 600,
    fontSize: 17,
    letterSpacing: 1,
    cursor: 'pointer',
    boxShadow: '0 4px 16px rgba(127,83,172,0.15)',
    transition: 'background 0.2s, transform 0.1s',
  };

  const wowGlow = {
    position: 'absolute',
    top: '-60px',
    left: '-60px',
    width: 180,
    height: 180,
    background: 'radial-gradient(circle, #7f53ac 0%, #657ced 80%, transparent 100%)',
    filter: 'blur(30px)',
    zIndex: 0,
    opacity: 0.5,
  };

  const wowGlow2 = {
    position: 'absolute',
    bottom: '-60px',
    right: '-60px',
    width: 180,
    height: 180,
    background: 'radial-gradient(circle, #657ced 0%, #7f53ac 80%, transparent 100%)',
    filter: 'blur(30px)',
    zIndex: 0,
    opacity: 0.4,
  };

  return (
    <div style={bgStyle}>
      <div style={cardStyle}>
        <div style={wowGlow}></div>
        <div style={wowGlow2}></div>
        <h1 style={{
          fontWeight: 800,
          fontSize: 36,
          marginBottom: 8,
          letterSpacing: 2,
          color: '#7f53ac',
          zIndex: 1,
          position: 'relative',
          textShadow: '0 2px 8px #c3b6e6'
        }}>
          Connexion
        </h1>
        <div style={{
          fontSize: 18,
          color: '#657ced',
          marginBottom: 28,
          fontWeight: 500,
          zIndex: 1,
          position: 'relative',
          letterSpacing: 1
        }}>
          Bienvenue sur <span style={{ fontWeight: 700, color: '#7f53ac' }}>DASHBOED</span> App
        </div>
        <form onSubmit={handleSubmit} style={{ zIndex: 1, position: 'relative' }}>
          <input
            type="text"
            placeholder="Nom d'utilisateur"
            value={username}
            onChange={e => setUsername(e.target.value)}
            style={inputStyle}
            required
            autoFocus
          />
          <input
            type="password"
            placeholder="Mot de passe"
            value={password}
            onChange={e => setPassword(e.target.value)}
            style={inputStyle}
            required
          />
          {error && (
            <div style={{
              color: '#e74c3c',
              background: '#fff0f0',
              borderRadius: 6,
              padding: '8px 0',
              marginBottom: 12,
              fontWeight: 500,
              fontSize: 15,
              boxShadow: '0 1px 4px #f8d7da'
            }}>
              {error}
            </div>
          )}
          <button
            type="submit"
            disabled={loading}
            style={{
              ...buttonStyle,
              opacity: loading ? 0.7 : 1,
              transform: loading ? 'scale(0.98)' : 'scale(1)'
            }}
          >
            {loading ? 'Connexion...' : 'Se connecter'}
          </button>
        </form>
        <div style={{
          marginTop: 28,
          color: '#888',
          fontSize: 13,
          zIndex: 1,
          position: 'relative'
        }}>
          <em>
            Ce formulaire est reli√© au backend <span style={{ color: '#7f53ac', fontWeight: 600 }}>Node/Express/MySQL</span>.
          </em>
        </div>
      </div>
    </div>
  );
}

export default Login;
