import React, { useState, useEffect } from 'react';

export default function UsersPage() {
  const [users, setUsers] = useState([]);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user'); // Role par défaut
  const [editingUserId, setEditingUserId] = useState(null);
  const [editUsername, setEditUsername] = useState('');
  const [editPassword, setEditPassword] = useState('');
  const [editRole, setEditRole] = useState('user');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await fetch('http://localhost:4000/api/users');
      const data = await res.json();
      setUsers(data);
    } catch (error) {
      alert('Erreur lors du chargement des utilisateurs');
    }
  };

  const addUser = async () => {
    if (!username || !password) {
      alert('Veuillez remplir tous les champs');
      return;
    }
    try {
      await fetch('http://localhost:4000/api/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password, role }),
      });
      setUsername('');
      setPassword('');
      setRole('user');
      fetchUsers();
    } catch {
      alert('Erreur lors de l\'ajout');
    }
  };

  const startEdit = (user) => {
    setEditingUserId(user.id);
    setEditUsername(user.username);
    setEditPassword('');
    setEditRole(user.role || 'user');
  };

  const cancelEdit = () => {
    setEditingUserId(null);
    setEditUsername('');
    setEditPassword('');
    setEditRole('user');
  };

  const saveEdit = async () => {
    if (!editUsername) {
      alert('Le nom d\'utilisateur est obligatoire');
      return;
    }
    try {
      await fetch(`http://localhost:4000/api/users/${editingUserId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: editUsername, password: editPassword, role: editRole }),
      });
      cancelEdit();
      fetchUsers();
    } catch {
      alert('Erreur lors de la modification');
    }
  };

  const deleteUser = async (id) => {
    if (!window.confirm('Voulez-vous vraiment supprimer cet utilisateur ?')) return;
    try {
      await fetch(`http://localhost:4000/api/users/${id}`, {
        method: 'DELETE',
      });
      fetchUsers();
    } catch {
      alert('Erreur lors de la suppression');
    }
  };

  // Styles simplifiés mais modernes
  const styles = {
    container: { maxWidth: 800, margin: 'auto', padding: 20, fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" },
    formGroup: { marginBottom: 15, display: 'flex', gap: 10, alignItems: 'center' },
    input: { flex: 1, padding: 8, borderRadius: 4, border: '1px solid #ccc', fontSize: 16 },
    select: { padding: 8, borderRadius: 4, border: '1px solid #ccc', fontSize: 16 },
    buttonPrimary: {
      padding: '10px 20px',
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      borderRadius: 5,
      cursor: 'pointer',
      fontWeight: 'bold',
      fontSize: 16,
      transition: 'background-color 0.3s ease',
    },
    buttonPrimaryHover: { backgroundColor: '#0056b3' },
    buttonDanger: {
      padding: '6px 14px',
      backgroundColor: '#dc3545',
      color: 'white',
      border: 'none',
      borderRadius: 5,
      cursor: 'pointer',
      fontWeight: 'bold',
      fontSize: 14,
      marginLeft: 10,
    },
    table: { width: '100%', borderCollapse: 'collapse', marginTop: 20 },
    th: { borderBottom: '2px solid #ddd', padding: 10, textAlign: 'left', backgroundColor: '#f8f9fa' },
    td: { padding: 10, borderBottom: '1px solid #ddd' },
    actionButtons: { display: 'flex', gap: 10 },
  };

  return (
    <div style={styles.container}>
      <h1>Gestion des utilisateurs</h1>

      <div style={{ marginBottom: 30 }}>
        <h2>Ajouter un utilisateur</h2>
        <div style={styles.formGroup}>
          <input
            type="text"
            placeholder="Nom d'utilisateur"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={styles.input}
          />
          <input
            type="password"
            placeholder="Mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={styles.input}
          />
          <select value={role} onChange={(e) => setRole(e.target.value)} style={styles.select}>
            <option value="user">Utilisateur</option>
            <option value="admin">Administrateur</option>
            <option value="moderator">Modérateur</option>
          </select>
          <button onClick={addUser} style={styles.buttonPrimary}>
            Ajouter
          </button>
        </div>
      </div>

      <h2>Liste des utilisateurs</h2>
      <table style={styles.table}>
        <thead>
          <tr>
            <th style={styles.th}>ID</th>
            <th style={styles.th}>Nom d'utilisateur</th>
            <th style={styles.th}>Rôle</th>
            <th style={styles.th}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 && (
            <tr>
              <td colSpan={4} style={{ padding: 20, textAlign: 'center' }}>
                Aucun utilisateur trouvé.
              </td>
            </tr>
          )}

          {users.map((user) => (
            <tr key={user.id}>
              <td style={styles.td}>{user.id}</td>
              <td style={styles.td}>
                {editingUserId === user.id ? (
                  <input
                    value={editUsername}
                    onChange={(e) => setEditUsername(e.target.value)}
                    style={styles.input}
                  />
                ) : (
                  user.username
                )}
              </td>
              <td style={styles.td}>
                {editingUserId === user.id ? (
                  <select
                    value={editRole}
                    onChange={(e) => setEditRole(e.target.value)}
                    style={styles.select}
                  >
                    <option value="user">Utilisateur</option>
                    <option value="admin">Administrateur</option>
                    <option value="moderator">Modérateur</option>
                  </select>
                ) : (
                  user.role || 'Utilisateur'
                )}
              </td>
              <td style={styles.td}>
                {editingUserId === user.id ? (
                  <>
                    <input
                      type="password"
                      placeholder="Nouveau mot de passe (optionnel)"
                      value={editPassword}
                      onChange={(e) => setEditPassword(e.target.value)}
                      style={{ ...styles.input, width: 180, marginRight: 10 }}
                    />
                    <button onClick={saveEdit} style={styles.buttonPrimary}>
                      Sauvegarder
                    </button>
                    <button onClick={cancelEdit} style={styles.buttonDanger}>
                      Annuler
                    </button>
                  </>
                ) : (
                  <>
                    <button onClick={() => startEdit(user)} style={styles.buttonPrimary}>
                      Modifier
                    </button>
                    <button onClick={() => deleteUser(user.id)} style={styles.buttonDanger}>
                      Supprimer
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
