import React, { useState } from 'react';
import axios from 'axios';

export default function reports() {
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');

  const handleSubmit = async e => {
    e.preventDefault();

    const token = localStorage.getItem('token'); // token JWT

    try {
      await axios.post(
        'http://localhost:4000/api/reports',
        { message }, // on envoie juste le message
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );
      setStatus('Rapport envoyé avec succès.');
      setMessage('');
    } catch (error) {
      console.error(error);
      setStatus('Erreur lors de la soumission.');
    }
  };

  return (
    <div>
      <h2>Envoyer un rapport</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={message}
          onChange={e => setMessage(e.target.value)}
          placeholder="Votre message ici"
          required
          rows={5}
          cols={50}
        />
        <br />
        <button type="submit">Envoyer</button>
      </form>
      <p>{status}</p>
    </div>
  );
}
