import React, { useEffect, useState } from 'react';

export default function Reports() {
  const [reports, setReports] = useState([]);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Récupérer le token JWT stocké (ex: après login)
  const token = localStorage.getItem('token');

  // Fonction pour charger les rapports
  const fetchReports = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await fetch('/api/reports', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!res.ok) {
        throw new Error('Erreur lors de la récupération des rapports');
      }
      const data = await res.json();
      setReports(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  // Fonction pour ajouter un nouveau rapport
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Ici, on suppose que l'user_id est dans le token (ou ailleurs), par exemple 'user_id' = 1
    // Pour un vrai cas, il faut extraire user_id du token ou du contexte utilisateur
    const user_id = 1;

    if (!message.trim()) {
      setError('Le message ne peut pas être vide.');
      return;
    }

    try {
      const res = await fetch('/api/reports', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ user_id, message }),
      });
      if (!res.ok) {
        const errData = await res.json();
        throw new Error(errData.message || 'Erreur lors de l\'ajout du rapport');
      }
      setMessage('');
      fetchReports(); // Recharger la liste
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Liste des Rapports</h2>
      {loading && <p>Chargement...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <ul>
        {reports.map((report) => (
          <li key={report.id}>
            <strong>User {report.user_id}:</strong> {report.message}
          </li>
        ))}
      </ul>

      <h3>Ajouter un nouveau rapport</h3>
      <form onSubmit={handleSubmit}>
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          rows={4}
          cols={50}
          placeholder="Votre message..."
        />
        <br />
        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
}
