import React, { useState, useEffect } from 'react';

export default function UsersPage() {
  const [users, setUsers] = useState([]);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [editingUserId, setEditingUserId] = useState(null);
  const [editUsername, setEditUsername] = useState('');
  const [editPassword, setEditPassword] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  // Charger les utilisateurs
  const fetchUsers = async () => {
    try {
      const res = await fetch('http://localhost:4000/api/users');
      const data = await res.json();
      setUsers(data);
    } catch (error) {
      alert('Erreur lors du chargement des utilisateurs');
    }
  };

  // Ajouter un utilisateur
  const addUser = async () => {
    if (!username || !password) {
      alert('Veuillez remplir tous les champs');
      return;
    }
    try {
      await fetch('http://localhost:4000/api/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });
      setUsername('');
      setPassword('');
      fetchUsers();
    } catch {
      alert('Erreur lors de l\'ajout');
    }
  };

  // DÃ©but modification utilisateur
  const startEdit = (user) => {
    setEditingUserId(user.id);
    setEditUsername(user.username);
    setEditPassword('');
  };

  // Annuler modification
  const cancelEdit = () => {
    setEditingUserId(null);
    setEditUsername('');
    setEditPassword('');
  };

  // Sauvegarder modification
  const saveEdit = async () => {
    if (!editUsername) {
      alert('Le nom d\'utilisateur est obligatoire');
      return;
    }
    try {
      await fetch(`http://localhost:4000/api/users/${editingUserId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: editUsername, password: editPassword }),
      });
      cancelEdit();
      fetchUsers();
    } catch {
      alert('Erreur lors de la modification');
    }
  };

  // Supprimer utilisateur
  const deleteUser = async (id) => {
    if (!window.confirm('Voulez-vous vraiment supprimer cet utilisateur ?')) return;
    try {
      await fetch(`http://localhost:4000/api/users/${id}`, {
        method: 'DELETE',
      });
      fetchUsers();
    } catch {
      alert('Erreur lors de la suppression');
    }
  };

  return (
    <div style={{ maxWidth: 700, margin: 'auto', padding: 20 }}>
      <h1>Gestion des utilisateurs</h1>

      <div style={{ marginBottom: 20, border: '1px solid #ccc', padding: 15, borderRadius: 8 }}>
        <h2>Ajouter un utilisateur</h2>
        <input
          type="text"
          placeholder="Nom d'utilisateur"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ padding: 8, marginRight: 10, width: '40%' }}
        />
        <input
          type="password"
          placeholder="Mot de passe"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ padding: 8, marginRight: 10, width: '40%' }}
        />
        <button onClick={addUser} style={{ padding: '8px 16px' }}>
          Ajouter
        </button>
      </div>

      <h2>Liste des utilisateurs</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0' }}>
            <th style={{ border: '1px solid #ddd', padding: 8 }}>ID</th>
            <th style={{ border: '1px solid #ddd', padding: 8 }}>Nom d'utilisateur</th>
            <th style={{ border: '1px solid #ddd', padding: 8 }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id} style={{ borderBottom: '1px solid #ddd' }}>
              <td style={{ border: '1px solid #ddd', padding: 8 }}>{user.id}</td>
              <td style={{ border: '1px solid #ddd', padding: 8 }}>
                {editingUserId === user.id ? (
                  <input
                    value={editUsername}
                    onChange={(e) => setEditUsername(e.target.value)}
                    style={{ padding: 6, width: '90%' }}
                  />
                ) : (
                  user.username
                )}
              </td>
              <td style={{ border: '1px solid #ddd', padding: 8 }}>
                {editingUserId === user.id ? (
                  <>
                    <input
                      type="password"
                      placeholder="Nouveau mot de passe (optionnel)"
                      value={editPassword}
                      onChange={(e) => setEditPassword(e.target.value)}
                      style={{ padding: 6, marginRight: 8 }}
                    />
                    <button onClick={saveEdit} style={{ marginRight: 8 }}>
                      Sauvegarder
                    </button>
                    <button onClick={cancelEdit}>Annuler</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => startEdit(user)} style={{ marginRight: 8 }}>
                      Modifier
                    </button>
                    <button onClick={() => deleteUser(user.id)} style={{ color: 'red' }}>
                      Supprimer
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}

          {users.length === 0 && (
            <tr>
              <td colSpan={3} style={{ padding: 12, textAlign: 'center' }}>
                Aucun utilisateur trouvÃ©.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
