import React, { useState } from 'react';

export default function Accueil() {
  const [email, setEmail] = useState('');
  const [date, setDate] = useState('');
  const [search, setSearch] = useState('');

  return (
    <div
      style={{
        maxWidth: '900px',
        margin: '0 auto',
        padding: '60px 20px',
        textAlign: 'center',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        color: '#2c3e50',
      }}
    >
      <h1 style={{ fontSize: '32px', marginBottom: '20px' }}>
        Rejoignez notre plateforme et trouvez votre h√¥tel id√©al
      </h1>

      <p style={{ fontSize: '18px', marginBottom: '40px' }}>
        Trouvez, r√©servez et g√©rez vos s√©jours facilement, en quelques clics.
      </p>

      {/* Champ de recherche */}
      <input
        type="text"
        placeholder="O√π souhaitez-vous aller ?"
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{
          padding: '15px',
          width: '60%',
          borderRadius: '6px',
          border: '1px solid #ccc',
          fontSize: '16px',
          marginBottom: '20px',
        }}
      />

      {/* S√©lecteur de date */}
      <div style={{ marginTop: '20px', marginBottom: '40px' }}>
        <input
          type="date"
          value={date}
          onChange={e => setDate(e.target.value)}
          style={{
            padding: '15px',
            borderRadius: '6px',
            border: '1px solid #ccc',
            fontSize: '16px',
          }}
        />
      </div>

      {/* Bouton d'action */}
      <button
        style={{
          backgroundColor: '#0071c2',
          color: '#fff',
          padding: '15px 40px',
          border: 'none',
          borderRadius: '8px',
          fontSize: '18px',
          cursor: 'pointer',
        }}
      >
        üîç Rechercher
      </button>

      {/* T√©moignage */}
      <section style={{ marginTop: '60px' }}>
        <h2>Ce que disent nos h√¥tes</h2>
        <p style={{ fontSize: '18px', fontStyle: 'italic', marginTop: '20px' }}>
          ¬´ J'ai pu m'inscrire en 15 minutes. Moins de 2 heures plus tard, j'avais ma premi√®re r√©servation ! ¬ª
        </p>
      </section>

      {/* Sponsors / partenaires */}
      <section style={{ marginTop: '60px' }}>
        <h2>Nos partenaires</h2>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '40px', marginTop: '20px' }}>
          <img src="/sponsor1.png" alt="Sponsor 1" style={{ height: '50px' }} />
          <img src="/sponsor2.png" alt="Sponsor 2" style={{ height: '50px' }} />
          <img src="/sponsor3.png" alt="Sponsor 3" style={{ height: '50px' }} />
        </div>
      </section>
    </div>
  );
}
