// Pourquoi j'ai une erreur quand je remplis le username et le mot de passe ?
// Explications et pistes de debug ci-dessous.

import React, { useState } from 'react';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Fonction de soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // On envoie la requête POST au backend Express
      const response = await fetch('http://localhost:4000/api/users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      // Si la réponse n'est pas du JSON valide, ça peut planter ici
      let data;
      try {
        data = await response.json();
      } catch (jsonErr) {
        setError("Réponse du serveur invalide. Vérifiez que le backend fonctionne et renvoie du JSON.");
        setLoading(false);
        return;
      }

      if (response.ok) {
        // Le backend doit renvoyer { role: ... }
        if (data.role === 'admin') {
          window.location.href = '/admin';
        } else if (data.role === 'user') {
          window.location.href = '/user';
        } else {
          setError('Rôle utilisateur inconnu ou non renvoyé par le backend.');
        }
      } else {
        // Affiche le message d'erreur du backend (ex: mauvais mot de passe)
        setError(data.message || 'Échec de la connexion.');
      }
    } catch (err) {
      // Erreur réseau (backend non démarré, mauvais port, etc)
      console.error('Erreur réseau ou fetch:', err);
      if (err.message && err.message.includes('ERR_CONNECTION_REFUSED')) {
        setError('Le serveur backend n\'est pas démarré. Veuillez démarrer le serveur sur le port 4000.');
      } else {
        setError('Erreur réseau. Vérifiez que le backend Express fonctionne sur http://localhost:4000.');
      }
    }
    setLoading(false);
  };

  // --- POURQUOI UNE ERREUR PEUT ARRIVER ? ---
  //
  // 1. Le backend n'est pas démarré (le port 4000 n'écoute pas)
  //    => Solution : démarre le backend (npm start dans Backend)
  //
  // 2. Le backend renvoie une erreur (ex: mauvais username/mot de passe)
  //    => Vérifie les logs du backend, regarde le message d'erreur affiché
  //
  // 3. Le backend ne renvoie pas du JSON valide
  //    => Ajoute un console.log côté backend pour voir la réponse
  //
  // 4. Mauvais port ou URL dans le fetch
  //    => Vérifie que l'URL correspond bien à celle du backend
  //
  // 5. Problème de CORS (rare en local, mais possible)
  //    => Vérifie que le backend autorise les requêtes du frontend
  //
  // 6. Problème MySQL (backend ne peut pas se connecter à la base)
  //    => Regarde les logs du backend pour voir l'erreur SQL éventuelle
  //
  // Pour débugger : 
  // - Ouvre la console du navigateur (onglet Réseau/Network) pour voir la requête et la réponse.
  // - Ouvre le terminal où tourne le backend pour voir les logs d'erreur éventuels.

  const bgStyle = {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #7f53ac 0%, #657ced 100%)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: "'Poppins', sans-serif",
  };

  const cardStyle = {
    background: 'rgba(255,255,255,0.95)',
    boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.37)',
    borderRadius: 24,
    padding: '40px 32px',
    maxWidth: 380,
    width: '100%',
    textAlign: 'center',
    position: 'relative',
    overflow: 'hidden',
  };

  const inputStyle = {
    width: '100%',
    padding: '12px 16px',
    margin: '10px 0 20px 0',
    border: 'none',
    borderRadius: 8,
    background: '#f3f6fd',
    fontSize: 16,
    outline: 'none',
    boxShadow: '0 2px 8px rgba(127,83,172,0.07)',
    transition: 'box-shadow 0.2s',
  };

  const buttonStyle = {
    width: '100%',
    padding: '14px',
    background: 'linear-gradient(90deg, #7f53ac 0%, #657ced 100%)',
    color: '#fff',
    border: 'none',
    borderRadius: 8,
    fontWeight: 600,
    fontSize: 17,
    letterSpacing: 1,
    cursor: 'pointer',
    boxShadow: '0 4px 16px rgba(127,83,172,0.15)',
    transition: 'background 0.2s, transform 0.1s',
  };

  const wowGlow = {
    position: 'absolute',
    top: '-60px',
    left: '-60px',
    width: 180,
    height: 180,
    background: 'radial-gradient(circle, #7f53ac 0%, #657ced 80%, transparent 100%)',
    filter: 'blur(30px)',
    zIndex: 0,
    opacity: 0.5,
  };

  const wowGlow2 = {
    position: 'absolute',
    bottom: '-60px',
    right: '-60px',
    width: 180,
    height: 180,
    background: 'radial-gradient(circle, #657ced 0%, #7f53ac 80%, transparent 100%)',
    filter: 'blur(30px)',
    zIndex: 0,
    opacity: 0.4,
  };

  return (
    <div style={bgStyle}>
      <div style={cardStyle}>
        <div style={wowGlow}></div>
        <div style={wowGlow2}></div>
        <h1 style={{
          fontWeight: 800,
          fontSize: 36,
          marginBottom: 8,
          letterSpacing: 2,
          color: '#7f53ac',
          zIndex: 1,
          position: 'relative',
          textShadow: '0 2px 8px #c3b6e6'
        }}>
          Connexion
        </h1>
        <div style={{
          fontSize: 18,
          color: '#657ced',
          marginBottom: 28,
          fontWeight: 500,
          zIndex: 1,
          position: 'relative',
          letterSpacing: 1
        }}>
          Bienvenue sur <span style={{ fontWeight: 700, color: '#7f53ac' }}>DASHBOED</span> App
        </div>
        <form onSubmit={handleSubmit} style={{ zIndex: 1, position: 'relative' }}>
          <input
            type="text"
            placeholder="Nom d'utilisateur"
            value={username}
            onChange={e => setUsername(e.target.value)}
            style={inputStyle}
            required
            autoFocus
          />
          <input
            type="password"
            placeholder="Mot de passe"
            value={password}
            onChange={e => setPassword(e.target.value)}
            style={inputStyle}
            required
          />
          {error && (
            <div style={{
              color: '#e74c3c',
              background: '#fff0f0',
              borderRadius: 6,
              padding: '8px 0',
              marginBottom: 12,
              fontWeight: 500,
              fontSize: 15,
              boxShadow: '0 1px 4px #f8d7da'
            }}>
              {error}
            </div>
          )}
          <button
            type="submit"
            disabled={loading}
            style={{
              ...buttonStyle,
              opacity: loading ? 0.7 : 1,
              transform: loading ? 'scale(0.98)' : 'scale(1)'
            }}
          >
            {loading ? 'Connexion...' : 'Se connecter'}
          </button>
        </form>
        <div style={{
          marginTop: 28,
          color: '#888',
          fontSize: 13,
          zIndex: 1,
          position: 'relative'
        }}>
          <em>
           <span style={{ color: '#7f53ac', fontWeight: 600 }}></span>.
          </em>
        </div>
      </div>
    </div>
  );
}

export default Login;
