import React, { useState, useEffect } from 'react';

export default function UserDashboard() {
  const [name, setName] = useState('');
  const [pricePerPerson, setPricePerPerson] = useState('');
  const [discount, setDiscount] = useState('');
  const [image, setImage] = useState(null);
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchHotels();
  }, []);

  const fetchHotels = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:4000/api/hotels', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        setHotels(data);
      } else {
        console.error('Erreur de chargement:', response.status);
      }
    } catch (error) {
      console.error('Erreur lors du fetch hotels:', error);
    }
  };

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      setImage(e.target.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!name || !pricePerPerson) {
      setError("Le nom et le prix sont obligatoires !");
      setLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('price', pricePerPerson); // ✔️ le champ doit être 'price' comme dans ton backend
    if (discount) formData.append('discount', discount);
    if (image) formData.append('images', image); // ✔️ correspond à upload.array('images')

    try {
      const token = localStorage.getItem('token'); // ✔️ récupère ton JWT ici
      const res = await fetch('http://localhost:4000/api/hotels', {
        method: 'POST',
        body: formData,
        headers: {
          Authorization: `Bearer ${token}`, // ✔️ envoie le token
        },
      });

      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`Erreur ${res.status} : ${errorText}`);
      }

      alert('Hôtel ajouté avec succès !');
      await fetchHotels();
      setName('');
      setPricePerPerson('');
      setDiscount('');
      setImage(null);
    } catch (err) {
      console.error(err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const discountedPrice = (price, discount) => {
    if (!discount) return price;
    return (price - price * (discount / 100)).toFixed(2);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Gestion des Hôtels</h1>

      <div style={styles.formSection}>
        <h2 style={styles.sectionTitle}>Ajouter un hôtel</h2>

        {error && <p style={styles.error}>{error}</p>}

        <form onSubmit={handleSubmit} style={styles.form}>
          <input
            type="text"
            placeholder="Nom de l'hôtel"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={styles.input}
            disabled={loading}
          />

          <input
            type="number"
            placeholder="Prix par personne"
            value={pricePerPerson}
            onChange={(e) => setPricePerPerson(e.target.value)}
            style={styles.input}
            disabled={loading}
          />

          <input
            type="number"
            placeholder="Remise (%)"
            value={discount}
            onChange={(e) => setDiscount(e.target.value)}
            style={styles.input}
            disabled={loading}
          />

          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            style={styles.input}
            disabled={loading}
          />

          {image && (
            <img
              src={URL.createObjectURL(image)}
              alt="preview"
              style={{ maxWidth: '200px', marginTop: '10px' }}
            />
          )}

          <button
            type="submit"
            style={styles.button}
            disabled={loading}
          >
            {loading ? 'Ajout en cours...' : 'Ajouter'}
          </button>
        </form>
      </div>

      <div style={styles.listSection}>
        <h2 style={styles.sectionTitle}>Hôtels ({hotels.length})</h2>
        {hotels.map((hotel) => (
          <div key={hotel.id} style={styles.card}>
            <h3>{hotel.name}</h3>
            <p>Prix : {hotel.price} DT</p>
            {hotel.discount > 0 && (
              <p>
                Après remise : {discountedPrice(hotel.price, hotel.discount)} DT
                ({hotel.discount}%)
              </p>
            )}
            {hotel.images && (
              <img
                src={`http://localhost:4000/uploads/${hotel.images}`}
                alt={hotel.name}
                style={{ width: '200px' }}
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: { maxWidth: 800, margin: '0 auto', padding: 20 },
  title: { textAlign: 'center' },
  formSection: { marginBottom: 40 },
  sectionTitle: { marginBottom: 10 },
  form: { display: 'flex', flexDirection: 'column', gap: 10 },
  input: { padding: 10, fontSize: 16 },
  button: { padding: 10, fontSize: 16, background: '#007bff', color: 'white' },
  listSection: { marginTop: 40 },
  card: {
    border: '1px solid #ddd',
    padding: 20,
    borderRadius: 8,
    marginBottom: 20,
  },
  error: { color: 'red' },
};
