import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [content, setContent] = useState('dashboard'); // dashboard | users | chatbot ...

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // Contenu dynamique en fonction de `content`
  const renderContent = () => {
    switch (content) {
      case 'dashboard':
        return (
          <>
            <h1>Dashboard</h1>
            <p>Statistiques, graphiques, infos importantes ici.</p>
          </>
        );
      case 'users':
        return (
          <>
            <h1>Gestion des utilisateurs</h1>
            <p>Ici tu peux gérer tous les utilisateurs.</p>
            {/* Tu peux intégrer ton composant UsersPage ici */}
          </>
        );
      case 'chatbot':
        return (
          <>
            <h1>Chatbot</h1>
            <p>Interface du robot chatbot ici.</p>
            {/* Ton composant chatbot si tu en as un */}
          </>
        );
      default:
        return <p>Choisis une option dans le menu.</p>;
    }
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      {/* Sidebar */}
      <nav
        style={{
          width: 220,
          backgroundColor: '#3b3f6b',
          color: 'white',
          padding: 20,
          display: 'flex',
          flexDirection: 'column',
          gap: 15,
        }}
      >
        <h2 style={{ marginBottom: 30 }}>Admin Panel</h2>

        <button style={menuButton} onClick={() => setContent('dashboard')}>
          📊 Dashboard
        </button>
        <button style={menuButton} onClick={() => setContent('users')}>
          👥 Utilisateurs
        </button>
        <button style={menuButton} onClick={() => setContent('chatbot')}>
          🤖 Chatbot
        </button>
        <button style={menuButton} onClick={() => alert('Notifications')}>
          🔔 Notifications
        </button>
        <button style={menuButton} onClick={() => alert('Messages')}>
          ✉️ Messages
        </button>
        <button style={menuButton} onClick={() => alert('Report')}>
          📄 Report
        </button>

        <div style={{ marginTop: 'auto' }}>
          <button
            style={{ ...menuButton, backgroundColor: '#d9534f' }}
            onClick={handleLogout}
            onMouseOver={e => (e.currentTarget.style.backgroundColor = '#c9302c')}
            onMouseOut={e => (e.currentTarget.style.backgroundColor = '#d9534f')}
          >
            🚪 Déconnexion
          </button>
        </div>
      </nav>

      {/* Main content dynamique */}
      <main style={{ flex: 1, padding: 30 }}>
        {renderContent()}
      </main>
    </div>
  );
}

const menuButton = {
  backgroundColor: 'transparent',
  border: 'none',
  color: 'white',
  textAlign: 'left',
  padding: '10px 15px',
  fontSize: 18,
  cursor: 'pointer',
  borderRadius: 6,
  fontWeight: 'bold',
  transition: 'background-color 0.3s',
};
