import React, { useEffect, useState } from 'react';

export default function Reports() {
  const [reports, setReports] = useState([]);
  const [message, setMessage] = useState('');
  const [userId, setUserId] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const token = localStorage.getItem('token'); // Ton JWT stockÃ© aprÃ¨s login

  // Charger les rapports existants au montage
  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const res = await fetch('/api/reports', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!res.ok) {
        throw new Error('Erreur lors du chargement des rapports.');
      }

      const data = await res.json();
      setReports(data);
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  const handleAddReport = async (e) => {
    e.preventDefault();

    if (!userId || !message) {
      setError('Remplissez tous les champs.');
      return;
    }

    try {
      const res = await fetch('/api/reports', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ user_id: userId, message }),
      });

      if (!res.ok) {
        throw new Error('Erreur lors de l\'ajout du rapport.');
      }

      setMessage('');
      setUserId('');
      setSuccess('Rapport ajoutÃ© !');
      setError('');
      fetchReports();
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '40px auto', fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif' }}>
      <h1>ðŸ“„ Rapports</h1>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <form onSubmit={handleAddReport} style={{ marginBottom: '30px' }}>
        <input
          type="text"
          placeholder="ID Utilisateur"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          style={{ padding: '10px', marginRight: '10px', width: '120px' }}
        />
        <input
          type="text"
          placeholder="Message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          style={{ padding: '10px', marginRight: '10px', width: '250px' }}
        />
        <button type="submit" style={{ padding: '10px 20px', background: '#0071c2', color: '#fff', border: 'none', borderRadius: '5px' }}>
          âž• Ajouter
        </button>
      </form>

      <h2>ðŸ“‹ Liste des rapports :</h2>
      <ul>
        {reports.map((report) => (
          <li key={report.id} style={{ marginBottom: '10px' }}>
            <strong>ID Utilisateur:</strong> {report.user_id} <br />
            <strong>Message:</strong> {report.message}
          </li>
        ))}
      </ul>
    </div>
  );
}
