import React, { useState } from 'react';

export default function UserDashboard() {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [discount, setDiscount] = useState('');
  const [images, setImages] = useState([]); // tableau pour plusieurs fichiers
  const [hotels, setHotels] = useState([]);

  const handleImageChange = (e) => {
    if (e.target.files.length > 0) {
      setImages(Array.from(e.target.files));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name || !price) {
      alert('Nom et prix obligatoires');
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('price', price);
    if (discount) formData.append('discount', discount);
    images.forEach((file) => formData.append('images', file));

    try {
      const token = localStorage.getItem('token'); // ou autre source du token

      const res = await fetch('http://localhost:4000/api/hotels', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      if (!res.ok) throw new Error('Erreur lors de l\'ajout');

      alert('Hôtel ajouté avec succès');

      // Ici tu peux soit fetch la liste, soit mettre à jour localement
      setHotels((prev) => [
        ...prev,
        {
          id: Date.now(),
          name,
          price: Number(price),
          discount: discount ? Number(discount) : 0,
          imageUrl: images.length > 0 ? URL.createObjectURL(images[0]) : null,
        },
      ]);

      // Reset form
      setName('');
      setPrice('');
      setDiscount('');
      setImages([]);
    } catch (err) {
      alert(err.message);
    }
  };

  const discountedPrice = (price, discount) => {
    if (!discount) return price;
    return (price - (price * discount) / 100).toFixed(2);
  };

  return (
    <div style={{ maxWidth: 700, margin: '40px auto', padding: 20 }}>
      <h1>Ajouter un hôtel</h1>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 15 }}>
        <input
          type="text"
          placeholder="Nom de l'hôtel"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Prix"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
          min="0"
        />
        <input
          type="number"
          placeholder="Remise (%)"
          value={discount}
          onChange={(e) => setDiscount(e.target.value)}
          min="0"
          max="100"
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          multiple
          required
        />
        <button type="submit">Ajouter</button>
      </form>

      <h2>Liste des hôtels</h2>
      {hotels.length === 0 ? (
        <p>Aucun hôtel enregistré</p>
      ) : (
        hotels.map((hotel) => (
          <div key={hotel.id} style={{ border: '1px solid #ccc', margin: 10, padding: 10 }}>
            {hotel.imageUrl && <img src={hotel.imageUrl} alt={hotel.name} style={{ maxWidth: 200 }} />}
            <h3>{hotel.name}</h3>
            <p>
              Prix :{' '}
              {hotel.discount > 0 ? (
                <>
                  <span style={{ textDecoration: 'line-through' }}>{hotel.price} DT</span>{' '}
                  <span style={{ fontWeight: 'bold', color: 'green' }}>
                    {discountedPrice(hotel.price, hotel.discount)} DT
                  </span>{' '}
                  <span style={{ color: 'red' }}>-{hotel.discount}%</span>
                </>
              ) : (
                <span>{hotel.price} DT</span>
              )}
            </p>
          </div>
        ))
      )}
    </div>
  );
}
