import React, { useState, useEffect } from 'react';

export default function HotelDashboard() {
  const [name, setName] = useState('');
  const [pricePerPerson, setPricePerPerson] = useState('');
  const [discount, setDiscount] = useState('');
  const [image, setImage] = useState(null);
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch hotels from backend on component mount
  useEffect(() => {
    fetchHotels();
  }, []);

  const fetchHotels = async () => {
    try {
      const response = await fetch('http://localhost:4000/api/hotels');
      if (response.ok) {
        const data = await response.json();
        setHotels(data);
      }
    } catch (error) {
      console.error('Error fetching hotels:', error);
    }
  };

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      setImage(e.target.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!name || !pricePerPerson) {
      setError("Le nom et le prix par personne sont obligatoires !");
      setLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('pricePerPerson', pricePerPerson);
    if (discount) formData.append('discount', discount);
    if (image) formData.append('image', image);

    try {
      console.log('Sending request to backend...');
      
      const res = await fetch('http://localhost:4000/api/hotels', {
        method: 'POST',
        body: formData,
      });

      console.log('Response status:', res.status);
      console.log('Response ok:', res.ok);

      if (!res.ok) {
        const errorText = await res.text();
        console.log('Error response:', errorText);
        throw new Error(`Erreur lors de l'ajout: ${res.status} ${errorText}`);
      }

      const result = await res.json();
      console.log('Success response:', result);
      
      setError('');
      alert('Hôtel ajouté avec succès !');

      // Refresh hotels list
      await fetchHotels();

      // Reset form
      setName('');
      setPricePerPerson('');
      setDiscount('');
      setImage(null);

    } catch (error) {
      console.error('Fetch error:', error);
      setError(`Erreur: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Calculate discounted price
  const discountedPrice = (price, discount) => {
    if (!discount) return price;
    return (price - price * (discount / 100)).toFixed(2);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Gestion des Hôtels</h1>
      
      {/* Add Hotel Form */}
      <div style={styles.formSection}>
        <h2 style={styles.sectionTitle}>Ajouter un hôtel</h2>
        
        {error && (
          <div style={styles.errorMessage}>
            {error}
          </div>
        )}
        
        <div style={styles.form}>
          <input
            type="text"
            placeholder="Nom de l'hôtel"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={styles.input}
            required
            disabled={loading}
          />
          
          <input
            type="number"
            placeholder="Prix par personne (DT)"
            value={pricePerPerson}
            onChange={(e) => setPricePerPerson(e.target.value)}
            style={styles.input}
            required
            min="0"
            step="0.01"
            disabled={loading}
          />
          
          <input
            type="number"
            placeholder="Remise (en %)"
            value={discount}
            onChange={(e) => setDiscount(e.target.value)}
            style={styles.input}
            min="0"
            max="100"
            disabled={loading}
          />
          
          <div style={styles.fileInputContainer}>
            <label style={styles.fileLabel}>
              Image de l'hôtel:
            </label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              style={styles.fileInput}
              disabled={loading}
            />
          </div>
          
          {image && (
            <div style={styles.previewContainer}>
              <strong>Image sélectionnée :</strong>
              <img
                src={URL.createObjectURL(image)}
                alt="aperçu"
                style={styles.previewImage}
              />
            </div>
          )}
          
          <button 
            onClick={handleSubmit}
            style={{
              ...styles.button,
              ...(loading ? styles.buttonDisabled : {}),
            }}
            disabled={loading}
          >
            {loading ? 'Ajout en cours...' : 'Ajouter l\'hôtel'}
          </button>
        </div>
      </div>

      {/* Hotels List */}
      <div style={styles.listSection}>
        <h2 style={styles.sectionTitle}>Liste des hôtels ({hotels.length})</h2>
        
        {hotels.length === 0 ? (
          <div style={styles.noHotels}>
            <p>Aucun hôtel enregistré</p>
            <small>Ajoutez votre premier hôtel en utilisant le formulaire ci-dessus</small>
          </div>
        ) : (
          <div style={styles.hotelGrid}>
            {hotels.map(hotel => (
              <div key={hotel.id} style={styles.hotelCard}>
                {hotel.imageUrl && (
                  <img 
                    src={hotel.imageUrl} 
                    alt={hotel.name} 
                    style={styles.hotelImage}
                    onError={(e) => {
                      e.target.style.display = 'none';
                    }}
                  />
                )}
                
                <div style={styles.hotelInfo}>
                  <h3 style={styles.hotelName}>{hotel.name}</h3>
                  
                  <div style={styles.priceSection}>
                    <span style={styles.priceLabel}>Prix par personne:</span>
                    {hotel.discount > 0 ? (
                      <div style={styles.discountedPriceContainer}>
                        <span style={styles.oldPrice}>{hotel.pricePerPerson} DT</span>
                        <span style={styles.newPrice}>
                          {discountedPrice(hotel.pricePerPerson, hotel.discount)} DT
                        </span>
                        <span style={styles.discountTag}>-{hotel.discount}%</span>
                      </div>
                    ) : (
                      <span style={styles.regularPrice}>{hotel.pricePerPerson} DT</span>
                    )}
                  </div>
                  
                  {hotel.created_at && (
                    <small style={styles.dateAdded}>
                      Ajouté le: {new Date(hotel.created_at).toLocaleDateString('fr-FR')}
                    </small>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: 1200,
    margin: '0 auto',
    padding: 20,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    backgroundColor: '#f5f5f5',
    minHeight: '100vh',
  },
  title: {
    textAlign: 'center',
    marginBottom: 40,
    color: '#333',
    fontSize: 28,
    fontWeight: 'bold',
  },
  formSection: {
    backgroundColor: 'white',
    padding: 30,
    borderRadius: 12,
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
    marginBottom: 40,
  },
  listSection: {
    backgroundColor: 'white',
    padding: 30,
    borderRadius: 12,
    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
  },
  sectionTitle: {
    color: '#333',
    marginBottom: 20,
    fontSize: 22,
    borderBottom: '2px solid #007bff',
    paddingBottom: 10,
  },
  errorMessage: {
    backgroundColor: '#f8d7da',
    color: '#721c24',
    padding: 12,
    borderRadius: 6,
    marginBottom: 20,
    border: '1px solid #f5c6cb',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
