import React, { useState } from 'react';
import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google';
import jwt_decode from 'jwt-decode';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Soumission du formulaire classique
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:4000/api/users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      let data;
      try {
        data = await response.json();
      } catch (jsonErr) {
        setError("Réponse du serveur invalide.");
        setLoading(false);
        return;
      }

      if (response.ok) {
        if (data.role === 'admin') {
          window.location.href = '/admin';
        } else if (data.role === 'user') {
          window.location.href = '/user';
        } else {
          setError('Rôle inconnu.');
        }
      } else {
        setError(data.message || 'Erreur de connexion.');
      }
    } catch (err) {
      console.error('Erreur réseau:', err);
      setError('Problème réseau ou backend hors ligne.');
    }
    setLoading(false);
  };

  // ✅ Connexion via Google
  const handleGoogleSuccess = async (credentialResponse) => {
    const token = credentialResponse.credential;
    const decoded = jwt_decode(token);
    console.log('Token décodé :', decoded);

    try {
      const response = await fetch('http://localhost:4000/api/users/google-login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ token }),
      });

      const data = await response.json();

      if (response.ok) {
        if (data.role === 'admin') {
          window.location.href = '/admin';
        } else if (data.role === 'user') {
          window.location.href = '/user';
        } else {
          setError('Rôle inconnu Google.');
        }
      } else {
        setError(data.message || 'Erreur Google Login.');
      }
    } catch (err) {
      console.error(err);
      setError('Erreur réseau Google Login.');
    }
  };

  const handleGoogleError = () => {
    setError('Connexion Google échouée.');
  };

  // Styles (inchangés) ...
  const bgStyle = { /* ... */ };
  const cardStyle = { /* ... */ };
  const inputStyle = { /* ... */ };
  const buttonStyle = { /* ... */ };
  const wowGlow = { /* ... */ };
  const wowGlow2 = { /* ... */ };

  return (
    <GoogleOAuthProvider clientId="TON_CLIENT_ID_GOOGLE">
      <div style={bgStyle}>
        <div style={cardStyle}>
          <div style={wowGlow}></div>
          <div style={wowGlow2}></div>
          <h1 style={{
            fontWeight: 800,
            fontSize: 36,
            marginBottom: 8,
            letterSpacing: 2,
            color: '#7f53ac',
            zIndex: 1,
            position: 'relative',
            textShadow: '0 2px 8px #c3b6e6'
          }}>
            Connexion
          </h1>
          <div style={{
            fontSize: 18,
            color: '#657ced',
            marginBottom: 28,
            fontWeight: 500,
            zIndex: 1,
            position: 'relative',
            letterSpacing: 1
          }}>
            Bienvenue sur <span style={{ fontWeight: 700, color: '#7f53ac' }}>DASHBOARD</span> App
          </div>

          <form onSubmit={handleSubmit} style={{ zIndex: 1, position: 'relative' }}>
            <input
              type="text"
              placeholder="Nom d'utilisateur"
              value={username}
              onChange={e => setUsername(e.target.value)}
              style={inputStyle}
              required
              autoFocus
            />
            <input
              type="password"
              placeholder="Mot de passe"
              value={password}
              onChange={e => setPassword(e.target.value)}
              style={inputStyle}
              required
            />
            {error && (
              <div style={{
                color: '#e74c3c',
                background: '#fff0f0',
                borderRadius: 6,
                padding: '8px 0',
                marginBottom: 12,
                fontWeight: 500,
                fontSize: 15,
                boxShadow: '0 1px 4px #f8d7da'
              }}>
                {error}
              </div>
            )}
            <button
              type="submit"
              disabled={loading}
              style={{
                ...buttonStyle,
                opacity: loading ? 0.7 : 1,
                transform: loading ? 'scale(0.98)' : 'scale(1)'
              }}
            >
              {loading ? 'Connexion...' : 'Se connecter'}
            </button>
          </form>

          <div style={{ margin: '20px 0' }}>
            <GoogleLogin
              onSuccess={handleGoogleSuccess}
              onError={handleGoogleError}
            />
          </div>

          <div style={{
            marginTop: 28,
            color: '#888',
            fontSize: 13,
            zIndex: 1,
            position: 'relative'
          }}>
            <em>Connectez-vous facilement avec Google.</em>
          </div>
        </div>
      </div>
    </GoogleOAuthProvider>
  );
}

export default Login;
