import React, { useState, useEffect } from 'react';

export default function UsersPage() {
  // Ton state existant
  const [users, setUsers] = useState([]);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user'); // Role par défaut
  const [editingUserId, setEditingUserId] = useState(null);
  const [editUsername, setEditUsername] = useState('');
  const [editPassword, setEditPassword] = useState('');
  const [editRole, setEditRole] = useState('user');

  // Nouveau state pour afficher/masquer formulaire
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  // ... tes fonctions fetchUsers, addUser, etc. ici inchangées

  // Fonction pour toggle l'affichage du formulaire
  const toggleForm = () => {
    setShowForm(!showForm);
  };

  // Styles existants
  const styles = {
    container: { maxWidth: 800, margin: 'auto', padding: 20, fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" },
    formGroup: { marginBottom: 15, display: 'flex', gap: 10, alignItems: 'center' },
    input: { flex: 1, padding: 8, borderRadius: 4, border: '1px solid #ccc', fontSize: 16 },
    select: { padding: 8, borderRadius: 4, border: '1px solid #ccc', fontSize: 16 },
    buttonPrimary: {
      padding: '10px 20px',
      backgroundColor: '#007bff',
      color: 'white',
      border: 'none',
      borderRadius: 5,
      cursor: 'pointer',
      fontWeight: 'bold',
      fontSize: 16,
      transition: 'background-color 0.3s ease',
    },
    buttonDanger: {
      padding: '6px 14px',
      backgroundColor: '#dc3545',
      color: 'white',
      border: 'none',
      borderRadius: 5,
      cursor: 'pointer',
      fontWeight: 'bold',
      fontSize: 14,
      marginLeft: 10,
    },
    table: { width: '100%', borderCollapse: 'collapse', marginTop: 20 },
    th: { borderBottom: '2px solid #ddd', padding: 10, textAlign: 'left', backgroundColor: '#f8f9fa' },
    td: { padding: 10, borderBottom: '1px solid #ddd' },
  };

  return (
    <div style={styles.container}>
      <h1>Gestion des utilisateurs</h1>

      {/* Bouton toggle pour afficher/masquer le formulaire */}
      <button onClick={toggleForm} style={styles.buttonPrimary}>
        {showForm ? 'Masquer le formulaire' : 'Ajouter un utilisateur'}
      </button>

      {/* Formulaire visible uniquement si showForm === true */}
      {showForm && (
        <div style={{ marginTop: 20, marginBottom: 30 }}>
          <h2>Ajouter un utilisateur</h2>
          <div style={styles.formGroup}>
            <input
              type="text"
              placeholder="Nom d'utilisateur"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={styles.input}
            />
            <input
              type="password"
              placeholder="Mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={styles.input}
            />
            <select value={role} onChange={(e) => setRole(e.target.value)} style={styles.select}>
              <option value="user">Utilisateur</option>
              <option value="admin">Administrateur</option>
              <option value="moderator">Modérateur</option>
            </select>
            <button onClick={addUser} style={styles.buttonPrimary}>
              Ajouter
            </button>
          </div>
        </div>
      )}

      {/* Tableau utilisateurs */}
      <h2>Liste des utilisateurs</h2>
      <table style={styles.table}>
        {/* ... reste du tableau inchangé */}
      </table>
    </div>
  );
}
