// src/pages/MainLayout.js
import React from 'react';
import { Link, Outlet } from 'react-router-dom';

export default function MainLayout() {
  return (
    <div style={{ display: 'flex' }}>
      <nav
        style={{
          width: '200px',
          backgroundColor: '#222',
          height: '100vh',
          padding: '20px',
          color: 'white',
          boxSizing: 'border-box',
        }}
      >
        <h2>Menu Utilisateur</h2>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li><Link to="/user" style={{ color: 'white' }}>Accueil</Link></li>
          <li><Link to="/user/search" style={{ color: 'white' }}>Recherche Hôtels</Link></li>
          <li><Link to="/user/report" style={{ color: 'white' }}>Rapport</Link></li>
          <li><Link to="/user/profile" style={{ color: 'white' }}>Profil</Link></li>
          <li><Link to="/user/bookings" style={{ color: 'white' }}>Réservations</Link></li>
          <li><Link to="/user/favorites" style={{ color: 'white' }}>Favoris</Link></li>
          <li><Link to="/user/contact" style={{ color: 'white' }}>Contact</Link></li>
          <li><Link to="/user/help" style={{ color: 'white' }}>Aide / FAQ</Link></li>
          <li><Link to="/user/settings" style={{ color: 'white' }}>Paramètres</Link></li>
        </ul>
      </nav>
      <main style={{ padding: '20px', flexGrow: 1 }}>
        <Outlet />
      </main>
    </div>
  );
}
