import React, { useState, useEffect } from 'react';

export default function UserDashboard() {
  const [name, setName] = useState('');
  const [pricePerPerson, setPricePerPerson] = useState('');
  const [discount, setDiscount] = useState('');
  const [image, setImage] = useState(null);
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Token fixe pour tests
  const token = 'ton_token_ici';

  useEffect(() => {
    fetchHotels();
  }, []);

  const fetchHotels = async () => {
    try {
      const response = await fetch('http://localhost:4000/api/hotels', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        setHotels(data);
      } else {
        console.error('Erreur lors de la récupération des hôtels:', response.status);
      }
    } catch (err) {
      console.error('Erreur fetchHotels:', err);
    }
  };

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      setImage(e.target.files[0]); // Un seul fichier
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!name || !pricePerPerson) {
      setError('Le nom et le prix par personne sont obligatoires !');
      return;
    }

    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('name', name);
      formData.append('price', pricePerPerson);
      if (discount) formData.append('discount', discount);
      if (image) formData.append('images', image); // nom du champ 'images' pour multer.single

      const res = await fetch('http://localhost:4000/api/hotels', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          // Ne PAS définir 'Content-Type', fetch le fait automatiquement
        },
        body: formData,
      });

      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`Erreur lors de l'ajout: ${res.status} ${errorText}`);
      }

      alert('Hôtel ajouté avec succès !');

      setName('');
      setPricePerPerson('');
      setDiscount('');
      setImage(null);

      fetchHotels();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 700, margin: 'auto', padding: 20 }}>
      <h1>Gestion des Hôtels</h1>

      {error && (
        <div style={{ color: 'red', marginBottom: 10 }}>
          <strong>{error}</strong>
        </div>
      )}

      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 15 }}>
        <input
          type="text"
          placeholder="Nom de l'hôtel"
          value={name}
          onChange={(e) => setName(e.target.value)}
          disabled={loading}
          required
          style={{ padding: 10, fontSize: 16 }}
        />

        <input
          type="number"
          placeholder="Prix par personne (DT)"
          value={pricePerPerson}
          onChange={(e) => setPricePerPerson(e.target.value)}
          min="0"
          step="0.01"
          disabled={loading}
          required
          style={{ padding: 10, fontSize: 16 }}
        />

        <input
          type="number"
          placeholder="Remise (en %)"
          value={discount}
          onChange={(e) => setDiscount(e.target.value)}
          min="0"
          max="100"
          disabled={loading}
          style={{ padding: 10, fontSize: 16 }}
        />

        <input type="file" accept="image/*" onChange={handleImageChange} disabled={loading} />

        {image && (
          <div style={{ marginTop: 10 }}>
            <strong>Image sélectionnée :</strong>
            <br />
            <img
              src={URL.createObjectURL(image)}
              alt="aperçu"
              style={{ maxWidth: '100%', maxHeight: 200, marginTop: 10, borderRadius: 8 }}
            />
          </div>
        )}

        <button type="submit" disabled={loading} style={{ padding: 15, fontSize: 16 }}>
          {loading ? 'Ajout en cours...' : 'Ajouter l\'hôtel'}
        </button>
      </form>

      <hr style={{ margin: '30px 0' }} />

      <h2>Liste des hôtels ({hotels.length})</h2>

      {hotels.length === 0 ? (
        <p>Aucun hôtel enregistré</p>
      ) : (
        <ul>
          {hotels.map((hotel) => (
            <li key={hotel.id} style={{ marginBottom: 15 }}>
              <h3>{hotel.name}</h3>
              <p>
                Prix : {hotel.price} DT{' '}
                {hotel.discount > 0 && <span>({hotel.discount}% de remise)</span>}
              </p>
              {hotel.images && (
                <img
                  src={`http://localhost:4000/uploads/${hotel.images.split(',')[0]}`}
                  alt={hotel.name}
                  style={{ maxWidth: 300, borderRadius: 8 }}
                  onError={(e) => (e.target.style.display = 'none')}
                />
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
