import React, { useState, useEffect } from 'react';

export default function UserPage() {
  const [users, setUsers] = useState([]); // Liste des utilisateurs
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');
  const [editingUserId, setEditingUserId] = useState(null);
  const [editUsername, setEditUsername] = useState('');
  const [editPassword, setEditPassword] = useState('');
  const [editRole, setEditRole] = useState('user');

  // Charger les utilisateurs au chargement de la page
  useEffect(() => {
    fetchUsers();
  }, []);

  async function fetchUsers() {
    try {
      const response = await fetch('http://localhost:4000/api/users');
      const data = await response.json();

      // Assure-toi que data est un tableau (selon ce que renvoie ton backend)
      if (Array.isArray(data)) {
        setUsers(data);
      } else if (Array.isArray(data.users)) {
        setUsers(data.users);
      } else {
        console.error('DonnÃ©es inattendues:', data);
        setUsers([]);
      }
    } catch (error) {
      alert('Erreur lors du chargement des utilisateurs');
    }
  }

  async function addUser() {
    if (!username || !password) {
      alert('Veuillez remplir tous les champs');
      return;
    }
    try {
      await fetch('http://localhost:4000/api/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password, role }),
      });
      setUsername('');
      setPassword('');
      setRole('user');
      fetchUsers();
    } catch {
      alert('Erreur lors de l\'ajout');
    }
  }

  function startEdit(user) {
    setEditingUserId(user.id);
    setEditUsername(user.username);
    setEditPassword('');
    setEditRole(user.role || 'user');
  }

  function cancelEdit() {
    setEditingUserId(null);
    setEditUsername('');
    setEditPassword('');
    setEditRole('user');
  }

  async function saveEdit() {
    if (!editUsername) {
      alert('Le nom d\'utilisateur est obligatoire');
      return;
    }
    try {
      await fetch(`http://localhost:4000/api/users/${editingUserId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: editUsername, password: editPassword, role: editRole }),
      });
      cancelEdit();
      fetchUsers();
    } catch {
      alert('Erreur lors de la modification');
    }
  }

  async function deleteUser(id) {
    if (!window.confirm('Voulez-vous vraiment supprimer cet utilisateur ?')) return;
    try {
      await fetch(`http://localhost:4000/api/users/${id}`, {
        method: 'DELETE',
      });
      fetchUsers();
    } catch {
      alert('Erreur lors de la suppression');
    }
  }

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <h1>Gestion des utilisateurs</h1>

      <div style={{ marginBottom: 30 }}>
        <h2>Ajouter un utilisateur</h2>
        <input
          type="text"
          placeholder="Nom d'utilisateur"
          value={username}
          onChange={e => setUsername(e.target.value)}
          style={{ padding: 8, marginRight: 10 }}
        />
        <input
          type="password"
          placeholder="Mot de passe"
          value={password}
          onChange={e => setPassword(e.target.value)}
          style={{ padding: 8, marginRight: 10 }}
        />
        <select value={role} onChange={e => setRole(e.target.value)} style={{ padding: 8, marginRight: 10 }}>
          <option value="user">Utilisateur</option>
          <option value="admin">Administrateur</option>
          <option value="moderator">ModÃ©rateur</option>
        </select>
        <button onClick={addUser}>Ajouter</button>
      </div>

      <h2>Liste des utilisateurs</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom d'utilisateur</th>
            <th>RÃ´le</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 ? (
            <tr><td colSpan={4}>Aucun utilisateur trouvÃ©.</td></tr>
          ) : (
            users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>
                  {editingUserId === user.id ? (
                    <input value={editUsername} onChange={e => setEditUsername(e.target.value)} />
                  ) : (
                    user.username
                  )}
                </td>
                <td>
                  {editingUserId === user.id ? (
                    <select value={editRole} onChange={e => setEditRole(e.target.value)}>
                      <option value="user">Utilisateur</option>
                      <option value="admin">Administrateur</option>
                      <option value="moderator">ModÃ©rateur</option>
                    </select>
                  ) : (
                    user.role
                  )}
                </td>
                <td>
                  {editingUserId === user.id ? (
                    <>
                      <input
                        type="password"
                        placeholder="Nouveau mot de passe (optionnel)"
                        value={editPassword}
                        onChange={e => setEditPassword(e.target.value)}
                      />
                      <button onClick={saveEdit}>Sauvegarder</button>
                      <button onClick={cancelEdit}>Annuler</button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => startEdit(user)}>Modifier</button>
                      <button onClick={() => deleteUser(user.id)}>Supprimer</button>
                    </>
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
