import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminReports() {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  // Charger les rapports depuis l'API
  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const response = await axios.get('http://localhost:4000/api/reports');
      setReports(response.data);
    } catch (error) {
      console.error('Erreur lors du chargement des rapports :', error);
    } finally {
      setLoading(false);
    }
  };

  // Supprimer un rapport
  const deleteReport = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/reports/${id}`);
      setReports(reports.filter((r) => r.id !== id));
    } catch (error) {
      console.error('Erreur lors de la suppression :', error);
    }
  };

  return (
    <div style={styles.container}>
      <h2>ðŸ“„ Liste des rapports</h2>

      {loading ? (
        <p>Chargement...</p>
      ) : reports.length === 0 ? (
        <p>Aucun rapport trouvÃ©.</p>
      ) : (
        <table style={styles.table}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Auteur</th>
              <th>Contenu</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {reports.map((report) => (
              <tr key={report.id}>
                <td>{report.id}</td>
                <td>{report.user}</td>
                <td>{report.message}</td>
                <td>{new Date(report.date).toLocaleString()}</td>
                <td>
                  <button style={styles.deleteButton} onClick={() => deleteReport(report.id)}>
                    Supprimer
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

const styles = {
  container: {
    padding: '20px',
    background: '#fff',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
  },
  table: {
    width: '100%',
    borderCollapse: 'collapse',
  },
  deleteButton: {
    backgroundColor: '#e74c3c',
    color: '#fff',
    padding: '5px 10px',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default AdminReports;
