import React, { useState } from 'react';

export default function UserDashboard() {
  const [name, setName] = useState('');
  const [pricePerPerson, setPricePerPerson] = useState('');
  const [discount, setDiscount] = useState('');
  const [image, setImage] = useState(null);
  const [hotels, setHotels] = useState([]);

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      setImage(e.target.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !pricePerPerson) {
      alert("Le nom et le prix par personne sont obligatoires !");
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('pricePerPerson', pricePerPerson);
    if (discount) formData.append('discount', discount);
    if (image) formData.append('image', image);

    try {
      // Appel à l'API backend
      const res = await fetch('http://localhost:4000/api/hotels', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) throw new Error('Erreur lors de l\'ajout');

      alert('Hôtel ajouté avec succès !');

      // Met à jour la liste des hôtels localement (ou fetch depuis backend)
      setHotels(prev => [
        ...prev,
        {
          id: Date.now(),
          name,
          pricePerPerson: Number(pricePerPerson),
          discount: discount ? Number(discount) : 0,
          imageUrl: image ? URL.createObjectURL(image) : null,
        },
      ]);

      // Reset form
      setName('');
      setPricePerPerson('');
      setDiscount('');
      setImage(null);
    } catch (error) {
      alert(error.message);
    }
  };

  // Calcule le prix après remise
  const discountedPrice = (price, discount) => {
    if (!discount) return price;
    return (price - price * (discount / 100)).toFixed(2);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Ajouter un hôtel</h1>
      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="text"
          placeholder="Nom de l'hôtel"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={styles.input}
          required
        />
        <input
          type="number"
          placeholder="Prix par personne"
          value={pricePerPerson}
          onChange={(e) => setPricePerPerson(e.target.value)}
          style={styles.input}
          required
          min="0"
        />
        <input
          type="number"
          placeholder="Solde (en %)"
          value={discount}
          onChange={(e) => setDiscount(e.target.value)}
          style={styles.input}
          min="0"
          max="100"
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          style={styles.fileInput}
          required
        />
        {image && (
          <div style={styles.previewContainer}>
            <strong>Image sélectionnée :</strong>
            <img
              src={URL.createObjectURL(image)}
              alt="aperçu"
              style={styles.previewImage}
            />
          </div>
        )}
        <button type="submit" style={styles.button}>
          Ajouter
        </button>
      </form>

      <h2 style={{ marginTop: 40, textAlign: 'center' }}>Liste des hôtels</h2>
      {hotels.length === 0 ? (
        <p style={{ textAlign: 'center' }}>Aucun hôtel enregistré</p>
      ) : (
        <div style={styles.hotelList}>
          {hotels.map(hotel => (
            <div key={hotel.id} style={styles.hotelCard}>
              {hotel.imageUrl && (
                <img src={hotel.imageUrl} alt={hotel.name} style={styles.hotelImage} />
              )}
              <div style={styles.hotelInfo}>
                <h3>{hotel.name}</h3>
                <p>
                  Prix par personne :{' '}
                  {hotel.discount > 0 ? (
                    <>
                      <span style={styles.oldPrice}>{hotel.pricePerPerson} DT</span>{' '}
                      <span style={styles.newPrice}>
                        {discountedPrice(hotel.pricePerPerson, hotel.discount)} DT
                      </span>{' '}
                      <span style={styles.discountTag}>-{hotel.discount}%</span>
                    </>
                  ) : (
                    <span>{hotel.pricePerPerson} DT</span>
                  )}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: 700,
    margin: '40px auto',
    padding: 20,
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  title: {
    textAlign: 'center',
    marginBottom: 30,
    color: '#333',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: 15,
    backgroundColor: '#f7f7f7',
    padding: 20,
    borderRadius: 8,
    boxShadow: '0 2px 8px rgb(0 0 0 / 0.1)',
  },
  input: {
    padding: 12,
    borderRadius: 6,
    border: '1px solid #ccc',
    fontSize: 16,
  },
  fileInput: {
    marginTop: 10,
  },
  previewContainer: {
    marginTop: 10,
    textAlign: 'center',
  },
  previewImage: {
    maxWidth: '100%',
    maxHeight: 200,
    borderRadius: 6,
    marginTop: 5,
    objectFit: 'cover',
  },
  button: {
    padding: '12px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    fontWeight: 'bold',
    fontSize: 18,
    borderRadius: 6,
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
  buttonHover: {
    backgroundColor: '#0056b3',
  },
  hotelList: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
    gap: 20,
    marginTop: 20,
  },
  hotelCard: {
    border: '1px solid #ddd',
    borderRadius: 8,
    overflow: 'hidden',
    boxShadow: '0 1px 6px rgb(0 0 0 / 0.1)',
    backgroundColor: 'white',
  },
  hotelImage: {
    width: '100%',
    height: 150,
    objectFit: 'cover',
  },
  hotelInfo: {
    padding: 15,
  },
  oldPrice: {
    textDecoration: 'line-through',
    color: '#999',
    marginRight: 8,
  },
  newPrice: {
    color: 'green',
    fontWeight: 'bold',
  },
  discountTag: {
    backgroundColor: '#dc3545',
    color: 'white',
    padding: '2px 6px',
    borderRadius: 4,
    marginLeft: 8,
    fontWeight: 'bold',
    fontSize: 14,
  },
};
