import React, { useState } from 'react';

// Correction : connexion reliée au backend (email/password, endpoint correct)
function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const validateForm = () => {
    if (!email || !password) {
      setError('Veuillez saisir l\'email et le mot de passe.');
      return false;
    }
    
    // Validation supplémentaire pour s'assurer que les champs ne sont pas vides après trim
    if (email.trim() === '' || password.trim() === '') {
      setError('Veuillez saisir l\'email et le mot de passe.');
      return false;
    }
    
    // Validation basique de l'email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Veuillez saisir un email valide.');
      return false;
    }
    
    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!validateForm()) return;

    setLoading(true);
    try {
      // Appel au backend Express (endpoint correct)
      const response = await fetch('/api/Users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      if (response.ok) {
        // Stockage du rôle dans localStorage
        localStorage.setItem('role', data.role);
        
        // Redirection selon le rôle
        if (data.role === 'admin') {
          window.location.href = '/admin';
        } else if (data.role === 'user') {
          window.location.href = '/user';
        } else {
          setError('Rôle utilisateur inconnu.');
        }
      } else {
        setError(data.message || 'Échec de la connexion.');
      }
    } catch (err) {
      setError('Erreur réseau. Veuillez réessayer.');
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 400, margin: '50px auto', padding: 20, border: '1px solid #ccc', borderRadius: 8 }}>
      <h2>Connexion</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: 12 }}>
          <label>Email :</label>
          <input
            type="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            style={{ width: '100%', padding: 8, marginTop: 4 }}
            required
          />
        </div>
        <div style={{ marginBottom: 12 }}>
          <label>Mot de passe :</label>
          <input
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            style={{ width: '100%', padding: 8, marginTop: 4 }}
            required
          />
        </div>
        {error && <div style={{ color: 'red', marginBottom: 12 }}>{error}</div>}
        <button type="submit" disabled={loading} style={{ width: '100%', padding: 10 }}>
          {loading ? 'Connexion...' : 'Se connecter'}
        </button>
      </form>
    </div>
  );
}

export default Login;
