import { useNavigate, Link, Outlet } from 'react-router-dom';
import React from 'react';

export default function MainLayout() {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Exemple : supprimer token ou données utilisateur stockées
    localStorage.removeItem('token');
    // Rediriger vers login
    navigate('/login');
  };

  const links = [
    { to: '/user', label: 'Accueil' },
    { to: '/user/search', label: 'Recherche Hôtels' },
    { to: '/user/report', label: 'Rapport' },
    { to: '/user/profile', label: 'Profil' },
    { to: '/user/bookings', label: 'Réservations' },
    { to: '/user/favorites', label: 'Favoris' },
    { to: '/user/contact', label: 'Contact' },
    { to: '/user/help', label: 'Aide / FAQ' },
    { to: '/user/settings', label: 'Paramètres' },
  ];

  return (
    <div style={{ display: 'flex', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif", height: '100vh' }}>
      <nav
        style={{
          width: '220px',
          backgroundColor: '#2c3e50',
          padding: '30px 20px',
          boxSizing: 'border-box',
          color: '#ecf0f1',
          display: 'flex',
          flexDirection: 'column',
          gap: '20px',
          justifyContent: 'space-between',
        }}
      >
        <div>
          <h2 style={{ marginBottom: '30px', borderBottom: '2px solid #2980b9', paddingBottom: '10px' }}>Menu Utilisateur</h2>

          {links.map(({ to, label }) => (
            <Link
              key={to}
              to={to}
              style={{
                color: '#ecf0f1',
                textDecoration: 'none',
                fontSize: '16px',
                padding: '8px 12px',
                borderRadius: '5px',
                transition: 'background-color 0.3s, color 0.3s',
                display: 'block',
              }}
              onMouseEnter={e => {
                e.target.style.backgroundColor = '#2980b9';
                e.target.style.color = '#fff';
              }}
              onMouseLeave={e => {
                e.target.style.backgroundColor = 'transparent';
                e.target.style.color = '#ecf0f1';
              }}
            >
              {label}
            </Link>
          ))}
        </div>

        {/* Bouton déconnexion en bas */}
        <button
          onClick={handleLogout}
          style={{
            backgroundColor: '#c0392b',
            color: '#fff',
            border: 'none',
            padding: '10px 15px',
            fontSize: '16px',
            borderRadius: '6px',
            cursor: 'pointer',
            transition: 'background-color 0.3s',
          }}
          onMouseEnter={e => (e.target.style.backgroundColor = '#e74c3c')}
          onMouseLeave={e => (e.target.style.backgroundColor = '#c0392b')}
        >
          Déconnexion
        </button>
      </nav>

      <main style={{ padding: '30px', flexGrow: 1, backgroundColor: '#f5f7fa' }}>
        <Outlet />
      </main>
    </div>
  );
}
