import React, { useState } from 'react';
import axios from 'axios';

function Reports() {
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:4000/api/reports', { user_id: 1, message }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Rapport soumis avec succès!');
      setMessage(''); // Réinitialiser le champ de message après la soumission
    } catch (error) {
      console.error('Erreur lors de la soumission du rapport:', error);
      alert('Erreur lors de la soumission du rapport.');
    }
  };

  return (
    <div>
      <h1>Soumettre un Rapport</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Message:</label>
          <textarea value={message} onChange={(e) => setMessage(e.target.value)} required />
        </div>
        <button type="submit">Soumettre</button>
      </form>
    </div>
  );
}

export default Reports; // Assurez-vous que cette ligne est présente
