import React, { useState, useEffect } from 'react';

export default function UserDashboard() {
  const [name, setName] = useState('');
  const [pricePerPerson, setPricePerPerson] = useState('');
  const [discount, setDiscount] = useState('');
  const [images, setImages] = useState([]); // tableau de fichiers
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Ton token JWT (à remplacer par ta gestion auth)
  const token = 'TON_TOKEN_JWT_ICI';

  useEffect(() => {
    fetchHotels();
  }, []);

  const fetchHotels = async () => {
    try {
      const res = await fetch('http://localhost:4000/api/hotels', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error('Erreur récupération hôtels');
      const data = await res.json();
      setHotels(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleImageChange = (e) => {
    if (e.target.files.length > 0) {
      setImages(Array.from(e.target.files)); // Converti FileList en Array
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!name || !pricePerPerson) {
      setError('Nom et prix obligatoires');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('name', name);
    formData.append('price', pricePerPerson);
    if (discount) formData.append('discount', discount);

    images.forEach((file) => {
      formData.append('images', file); // champ 'images' côté backend upload.array
    });

    try {
      const res = await fetch('http://localhost:4000/api/hotels', {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` },
        body: formData, // ne PAS définir Content-Type, fetch le gère
      });
      if (!res.ok) {
        const errText = await res.text();
        throw new Error(errText || 'Erreur ajout hôtel');
      }
      alert('Hôtel ajouté avec succès !');
      setName('');
      setPricePerPerson('');
      setDiscount('');
      setImages([]);
      fetchHotels();
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 900, margin: 'auto', padding: 20 }}>
      <h1>Gestion des Hôtels</h1>

      {error && (
        <div style={{ backgroundColor: '#fdd', padding: 10, marginBottom: 15 }}>
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 15 }}>
        <input
          type="text"
          placeholder="Nom de l'hôtel"
          value={name}
          onChange={(e) => setName(e.target.value)}
          disabled={loading}
        />

        <input
          type="number"
          placeholder="Prix par personne"
          value={pricePerPerson}
          onChange={(e) => setPricePerPerson(e.target.value)}
          min="0"
          step="0.01"
          disabled={loading}
        />

        <input
          type="number"
          placeholder="Remise (%)"
          value={discount}
          onChange={(e) => setDiscount(e.target.value)}
          min="0"
          max="100"
          disabled={loading}
        />

        <input
          type="file"
          accept="image/*"
          multiple
          onChange={handleImageChange}
          disabled={loading}
        />

        {images.length > 0 && (
          <div style={{ display: 'flex', gap: 10, flexWrap: 'wrap' }}>
            {images.map((file, idx) => (
              <img
                key={idx}
                src={URL.createObjectURL(file)}
                alt="preview"
                style={{ width: 100, height: 100, objectFit: 'cover', borderRadius: 8 }}
              />
            ))}
          </div>
        )}

        <button type="submit" disabled={loading}>
          {loading ? 'Ajout en cours...' : 'Ajouter Hôtel'}
        </button>
      </form>

      <h2 style={{ marginTop: 40 }}>Liste des hôtels ({hotels.length})</h2>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill,minmax(250px,1fr))', gap: 20 }}>
        {hotels.map((hotel) => (
          <div key={hotel.id} style={{ border: '1px solid #ccc', borderRadius: 10, padding: 15 }}>
            {hotel.images && hotel.images.split(',').length > 0 && (
              <img
                src={`http://localhost:4000/uploads/${hotel.images.split(',')[0]}`}
                alt={hotel.name}
                style={{ width: '100%', height: 150, objectFit: 'cover', borderRadius: 10 }}
              />
            )}
            <h3>{hotel.name}</h3>
            <p>
              Prix : {hotel.price} DT
              {hotel.discount > 0 && (
                <span style={{ color: 'green', marginLeft: 10 }}>
                  -{hotel.discount}%
                </span>
              )}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
