import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function SearchHotels() {
  const [hotels, setHotels] = useState([]);
  const [maxPrice, setMaxPrice] = useState('');
  const [filteredHotels, setFilteredHotels] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Replace with your actual JWT token
  const token = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6MSwidXNlcm5hbWUiOiJub3VyIiwicm9sZSI6ImFkbWluIiwiaWF0IjoxNzUxOTE3MDkxLCJleHAiOjE3NTE5MjA2OTF9.3Yz4l4wnJIj01qkvR_6YFfJUEEpWmFPFbGqyH2qRnNU';

  useEffect(() => {
    fetchHotels();
  }, []);

  useEffect(() => {
    // Automatically filter when maxPrice changes
    if (maxPrice) {
      setFilteredHotels(
        hotels.filter(hotel => hotel.price <= parseFloat(maxPrice))
      );
    } else {
      setFilteredHotels(hotels);
    }
  }, [maxPrice, hotels]);

  async function fetchHotels() {
    try {
      const res = await fetch('http://localhost:4000/api/hotels', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      if (!res.ok) throw new Error('Erreur lors du chargement des h√¥tels');
      const data = await res.json();
      setHotels(data);
      setFilteredHotels(data);
    } catch (err) {
      console.error(err);
      setError('Impossible de charger les h√¥tels.');
    }
  }

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <h1>üîç Recherche d'H√¥tels</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <div style={{ marginBottom: 20 }}>
        <label>
          Prix maximum :{' '}
          <input
            type="number"
            value={maxPrice}
            placeholder="Ex : 200"
            onChange={e => setMaxPrice(e.target.value)}
          />
          <span> DT</span>
        </label>
      </div>
      <button
        onClick={() => navigate('/admin/userdashboard')}
        style={{
          padding: '10px 15px',
          backgroundColor: '#3498db',
          color: '#fff',
          border: 'none',
          borderRadius: 5,
          cursor: 'pointer',
          marginBottom: 20,
        }}
      >
        ‚ûï Aller vers Ajout Chauffeur / H√¥tel (Admin)
      </button>
      {filteredHotels.length === 0 ? (
        <p>Aucun h√¥tel trouv√© pour ce prix.</p>
      ) : (
        <ul>
          {filteredHotels.map(hotel => (
            <li key={hotel.id} style={{ marginBottom: 20 }}>
              <strong>{hotel.name}</strong> - {hotel.price} DT
              {hotel.discount > 0 && (
                <span> (-{hotel.discount}%)</span>
              )}
              {hotel.images && (
                <div>
                  <img
                    src={`http://localhost:4000/uploads/${hotel.images.split(',')[0]}`}
                    alt={hotel.name}
                    style={{ maxWidth: 200, borderRadius: 8, marginTop: 5 }}
                    onError={e => { e.target.style.display = 'none'; }}
                  />
                </div>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
