import React, { useState, useEffect } from 'react';

export default function UserPage() {
  const [hotels, setHotels] = useState([]);
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [discount, setDiscount] = useState('');
  const [images, setImages] = useState([]);

  // Charger les hôtels existants (si tu veux afficher une liste)
  useEffect(() => {
    fetchHotels();
  }, []);

  const fetchHotels = async () => {
    try {
      const res = await fetch('http://localhost:4000/api/hotels');
      const data = await res.json();
      setHotels(data);
    } catch (error) {
      alert('Erreur chargement hôtels');
    }
  };

  const handleImageChange = (e) => {
    setImages([...e.target.files]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !price) {
      alert("Le nom et le prix sont obligatoires !");
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('price', price);
    if (discount) formData.append('discount', discount);
    for (let i = 0; i < images.length; i++) {
      formData.append('images', images[i]);
    }

    try {
      const res = await fetch('http://localhost:4000/api/hotels', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) throw new Error('Erreur lors de l\'ajout');

      alert('Hôtel ajouté avec succès !');
      setName('');
      setPrice('');
      setDiscount('');
      setImages([]);
      fetchHotels();
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: 20 }}>
      <h1>Ajouter un hôtel</h1>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 15 }}>
        <input
          type="text"
          placeholder="Nom de l'hôtel"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />

        <input
          type="number"
          placeholder="Prix"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          required
        />

        <input
          type="number"
          placeholder="Solde (en %)"
          value={discount}
          onChange={(e) => setDiscount(e.target.value)}
          min="0"
          max="100"
        />

        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleImageChange}
        />

        <button type="submit">Ajouter</button>
      </form>

      <h2>Liste des hôtels</h2>
      {hotels.length === 0 ? (
        <p>Aucun hôtel enregistré</p>
      ) : (
        <ul>
          {hotels.map((hotel) => (
            <li key={hotel.id}>
              {hotel.name} - Prix: {hotel.price} DT {hotel.discount ? `(Solde: ${hotel.discount}%)` : ''}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
