import React, { useState } from 'react';

export default function UserDashboard() {
  const [name, setName] = useState('');
  const [pricePerPerson, setPricePerPerson] = useState('');
  const [discount, setDiscount] = useState('');
  const [image, setImage] = useState(null);

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      setImage(e.target.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !pricePerPerson) {
      alert("Le nom et le prix par personne sont obligatoires !");
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('pricePerPerson', pricePerPerson);
    if (discount) formData.append('discount', discount);
    if (image) formData.append('image', image);

    try {
      // Ici ta requête POST vers backend (exemple)
      const res = await fetch('http://localhost:4000/api/hotels', {
        method: 'POST',
        body: formData,
      });

      if (!res.ok) throw new Error('Erreur lors de l\'ajout');

      alert('Hôtel ajouté avec succès !');
      setName('');
      setPricePerPerson('');
      setDiscount('');
      setImage(null);
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: 'auto', padding: 20 }}>
      <h1>Ajouter un hôtel</h1>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 15 }}>
        <input
          type="text"
          placeholder="Nom de l'hôtel"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Prix par personne"
          value={pricePerPerson}
          onChange={(e) => setPricePerPerson(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Solde (en %)"
          value={discount}
          onChange={(e) => setDiscount(e.target.value)}
          min="0"
          max="100"
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          required
        />
        {image && (
          <div style={{ marginTop: 10 }}>
            <strong>Image sélectionnée :</strong> {image.name}
            <br />
            <img
              src={URL.createObjectURL(image)}
              alt="aperçu"
              style={{ maxWidth: '100%', maxHeight: 200, marginTop: 10 }}
            />
          </div>
        )}
        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
}
