import React from 'react';
import { useNavigate, Outlet } from 'react-router-dom';

const menuButton = {
  backgroundColor: 'transparent',
  color: '#2c3e50',
  border: 'none',
  padding: '12px 15px',
  fontSize: '14px',
  borderRadius: '0px',
  cursor: 'pointer',
  width: '100%',
  textAlign: 'left',
  marginBottom: '2px',
  transition: 'all 0.2s ease',
  display: 'flex',
  alignItems: 'center',
  gap: '10px',
};

export default function Main() {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div
      style={{
        display: 'flex',
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
        height: '100vh',
      }}
    >
      <nav
        style={{
          width: '220px',
          backgroundColor: '#ffffff',
          padding: '20px 0px',
          boxSizing: 'border-box',
          color: '#2c3e50',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between',
          boxShadow: '1px 0px 5px rgba(0,0,0,0.1)',
        }}
      >
        <div>
          <h2
            style={{
              padding: '0 20px',
              marginBottom: '25px',
              fontSize: '18px',
              fontWeight: '600',
              color: '#2980b9',
            }}
          >
            Mon Espace
          </h2>

          {[
            { icon: '🏠', label: 'Accueil', path: '/user' },
            { icon: '🔍', label: 'Recherche', path: '/user/search' },
            { icon: '📊', label: 'Rapports', path: '/reports/Reports' },
            { icon: '👤', label: 'Profil', path: '/user/profile' },
            { icon: '📅', label: 'Réservations', path: '/user/bookings' },
            { icon: '⭐', label: 'Favoris', path: '/user/favorites' },
            { icon: '📞', label: 'Contact', path: '/user/contact' },
            { icon: '❓', label: 'Aide', path: '/user/help' },
            { icon: '⚙️', label: 'Paramètres', path: '/user/settings' },
          ].map((item, index) => (
            <button
              key={index}
              style={menuButton}
              onClick={() => navigate(item.path)}
              onMouseEnter={e => (e.target.style.backgroundColor = '#f5f7fa')}
              onMouseLeave={e => (e.target.style.backgroundColor = 'transparent')}
            >
              <span>{item.icon}</span>
              <span>{item.label}</span>
            </button>
          ))}
        </div>

        <div style={{ padding: '0 20px' }}>
          <button
            onClick={handleLogout}
            style={{
              backgroundColor: 'transparent',
              color: '#e74c3c',
              border: 'none',
              padding: '12px 15px',
              fontSize: '14px',
              cursor: 'pointer',
              transition: 'all 0.2s ease',
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              width: '100%',
            }}
            onMouseEnter={e => (e.target.style.backgroundColor = '#f5f7fa')}
            onMouseLeave={e => (e.target.style.backgroundColor = 'transparent')}
          >
            <span>🔓</span>
            <span>Déconnexion</span>
          </button>
        </div>
      </nav>

      <main
        style={{
          padding: '30px',
          flexGrow: 1,
          backgroundColor: '#f9f9f9',
          overflowY: 'auto',
        }}
      >
        <Outlet />
      </main>
    </div>
  );
}
