import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AdminDashboard() {
  const navigate = useNavigate();

  // État pour la liste des utilisateurs
  const [users, setUsers] = useState([]);
  // État pour formulaire ajout/modif
  const [form, setForm] = useState({ id: null, username: '', role: '' });
  const [error, setError] = useState('');

  // Charger la liste des utilisateurs au chargement du composant
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await fetch('http://localhost:4000/api/users');
      if (!res.ok) throw new Error('Erreur chargement utilisateurs');
      const data = await res.json();
      setUsers(data);
    } catch (err) {
      setError(err.message);
    }
  };

  // Ajouter ou modifier un utilisateur
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    const method = form.id ? 'PUT' : 'POST';
    const url = form.id
      ? `http://localhost:4000/api/users/${form.id}`
      : 'http://localhost:4000/api/users';

    try {
      const res = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: form.username, role: form.role }),
      });
      if (!res.ok) throw new Error('Erreur sauvegarde utilisateur');
      await fetchUsers();
      setForm({ id: null, username: '', role: '' });
    } catch (err) {
      setError(err.message);
    }
  };

  // Supprimer un utilisateur
  const handleDelete = async (id) => {
    if (!window.confirm('Voulez-vous vraiment supprimer cet utilisateur ?')) return;
    try {
      const res = await fetch(`http://localhost:4000/api/users/${id}`, {
        method: 'DELETE',
      });
      if (!res.ok) throw new Error('Erreur suppression utilisateur');
      await fetchUsers();
    } catch (err) {
      setError(err.message);
    }
  };

  // Modifier (pré-remplir formulaire)
  const handleEdit = (user) => {
    setForm(user);
  };

  // Déconnexion
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div style={{ padding: 20, maxWidth: 600, margin: 'auto' }}>
      <h1>Dashboard Admin</h1>
      <button onClick={handleLogout} style={{ marginBottom: 20 }}>
        Déconnexion
      </button>

      <h2>Liste des utilisateurs</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <table border="1" cellPadding="8" style={{ width: '100%', marginBottom: 20 }}>
        <thead>
          <tr>
            <th>Nom d'utilisateur</th>
            <th>Rôle</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.length === 0 ? (
            <tr><td colSpan="3">Aucun utilisateur</td></tr>
          ) : (
            users.map((user) => (
              <tr key={user.id}>
                <td>{user.username}</td>
                <td>{user.role}</td>
                <td>
                  <button onClick={() => handleEdit(user)} style={{ marginRight: 10 }}>
                    Modifier
                  </button>
                  <button onClick={() => handleDelete(user.id)} style={{ color: 'red' }}>
                    Supprimer
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      <h2>{form.id ? 'Modifier utilisateur' : 'Ajouter utilisateur'}</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: 40 }}>
        <input
          type="text"
          placeholder="Nom d'utilisateur"
          value={form.username}
          onChange={e => setForm({ ...form, username: e.target.value })}
          required
          style={{ marginRight: 10 }}
        />
        <select
          value={form.role}
          onChange={e => setForm({ ...form, role: e.target.value })}
          required
          style={{ marginRight: 10 }}
        >
          <option value="">-- Choisir un rôle --</option>
          <option value="admin">Admin</option>
          <option value="user">User</option>
        </select>
        <button type="submit">{form.id ? 'Enregistrer' : 'Ajouter'}</button>
        {form.id && (
          <button
            type="button"
            onClick={() => setForm({ id: null, username: '', role: '' })}
            style={{ marginLeft: 10 }}
          >
            Annuler
          </button>
        )}
      </form>
    </div>
  );
}

export default AdminDashboard;
